function CalendarController($scope, $rootScope, templateManager, ScreenService, ZhUtil, Query) {

	$scope.openField = function(field) {

		if (field.readOnly || field.aclReadOnly) {
			return;
		}

		var selectWidget = metaDataFactory.widgetFactory({
			"id": 0,
			"name": "Widget",
			"label": field.label,
			"template": "widget/calendar.html",
			"container": field.widget.container,
			"selectField": field,
			"isVisible": true,
			"popupNoBlock": false,
			"dataSource": field.dataSource,
			"dataSourceFilter": field.dataSourceFilter,
			"todayText": field.todayText || "Hoje",
			"dateField": field.dateField,
			"titleField": field.titleField,
			"dateFormat": field.dateFormat,
			"fields": [],
			"actions": [
				{
					"label": "Voltar",
					"showAsAction": "back",
					"isVisible": true,
					"showOnForm": true,
					"showOnList": true,
					"events": [
						{
							"name": "ActionEvent",
							"code": "ScreenService.closePopup()",
							"id": 9998
	          }
	        ]
	      },
				{
					"label": "OK",
					"showAsAction": "never",
					"isVisible": true,
					"showOnForm": true,
					"showOnList": true,
					"events": [
						{
							"name": "ActionEvent",
							"code": "templateManager.container.setMultipleSelected(args)",
							"id": 9999
	          }
	        ]
	      }
	    ],
			"events": [
				{
					"name": "WidgetAfterMoveRow",
					"code": "args.owner.setSelected(args);",
					"id": 9999
        }
      ]
		});

		selectWidget.setSelected = function(args) {
			ScreenService.closePopup();

			var row = args.row;
			var field = args.owner.selectField;

			if(field.change) {
				field.change();
			}

			field.widget.currentRow[field.name] = row;
			field.formatedValue = decodeDate(row, field);
		};

		ScreenService.openPopup(selectWidget);
	};

	$scope.clearField = function(field) {
		field.widget.currentRow[field.name] = "";
		field.formatedValue = "";
	};

	function decodeDate(date) {
		return date
			.split("-")
			.map(function(item) {
				var str = item;

				if(item.length == 1) {
					str = "0" + item;
				}

				return str;
			})
			.join("/");
	}
}


// FILE: js/components/DecodeValueField.js
var DecodeValueField = function($scope) {

	$scope.isCheckbox = function(field) {
		return field.template.indexOf("field/checkbox.html") !== -1;
	};

	$scope.isButton = function(field) {
		return field.template.indexOf("field/button.html") !== -1;
	};

	this.model = function(field, fieldRow) {
		if (field) {
			switch(field.template) {
		 		case 'field/select-native.html':
		 		case 'field/radio-group.html':
		 			viewRow = decodeSelectNative(field, fieldRow);
		 			break;
		 		case 'field/select-multiple.html':
		 			viewRow = decodeSelectMultiple(field, fieldRow);
		 			break;
		 		case 'field/password-edit.html':
		 		case 'field/password.html':
		 			viewRow = passwordView(fieldRow);
		 			break;
		 		case 'field/phone.html':
		 			viewRow = phoneView(fieldRow);
		 			break;
		 		default:
					if (field.mask && field.mask.type === "fix")
						viewRow = Util.applyMask(fieldRow, field.mask.params.mask);
					else
						viewRow = fieldRow;
					break;
			}
		}

		return viewRow;
	};

	function passwordView(fieldRow) {
		if(fieldRow)
			return fieldRow.replace(/./g, "*");
	}

	function phoneView(fieldRow){
		var finalNum = "";
		fieldRow = fieldRow.replace(/[^\d]/g, '');

		finalNum += '(' + fieldRow.slice(0,2) + ') ';

		if(fieldRow.length === 10) {
		  finalNum += fieldRow.slice(2,6) + '-';
		  finalNum += fieldRow.slice(6,10);
	  }

	  else if(fieldRow.length === 11) {
		  finalNum += fieldRow.slice(2,7) + '-';
		  finalNum += fieldRow.slice(7,11);
	  }

	  return finalNum;
	}

	function decodeSelectMultiple(field, fieldRow) {
		var viewRow = "";

		if (field && fieldRow && field.dataSource) {
			viewRow = field.dataSource.data
				.filter(function(item) {
					return fieldRow.indexOf(item[field.valueField]) !== -1;
				})
				.map(function(item) {
					return item[field.descriptionField];
				})
				.join(", ");
		}

		return viewRow;
	}

	function decodeSelectNative(field, fieldRow) {
		var viewRow = "";

		if (field && fieldRow && field.dataSource) {
			var found = false;
			var data = field.dataSource.data;

			for (var i = 0; i < data.length && !found; i++) {
				if (data[i][field.valueField] === fieldRow) {
					viewRow = data[i][field.descriptionField];
					found = true;
				}
			}
		}

		return viewRow;
	}
};


