// FILE: js/components/FormController.js
function FormController($scope) {

	var self = this;

	this.__defineGetter__('widget', function() {
		return $scope.widget;
	});

	function groupFieldsByFieldGroup(widgetFields, widgetFieldGroups) {
		if (widgetFields && widgetFields.length > 0) {

			if (widgetFieldGroups && widgetFieldGroups.length > 0) {
				widgetFieldGroups.forEach(function(fieldGroup) {
					if (fieldGroup.name) {
						var fields = widgetFields.filter(function(field) {
							return field.fieldGroup === fieldGroup.name && !angular.isUndefined(field.fieldGroup) && !angular.isUndefined(fieldGroup.name);
						});

						if (fields.length > 0) {
							fieldGroup.rows = groupFieldsByRowSize(fields, "class", 12);
						} else {
							fieldGroup.rows = [];
						}

					}
				});
			}

			var noFieldGroupInputs = widgetFields.filter(function(field) {
				return !field.fieldGroup;
			});
			if (noFieldGroupInputs.length > 0) {
				if (!Array.isArray(widgetFieldGroups)) {
					widgetFieldGroups = [];
				}
				var defaultFieldGroup = {
					opened: true,
					rows: groupFieldsByRowSize(noFieldGroupInputs, "class", 12)
				};
				noFieldGroupInputs.forEach(function(field) {
					field.fieldGroup = defaultFieldGroup;
				});

				widgetFieldGroups.unshift(defaultFieldGroup);
			}
		}

		return widgetFieldGroups;
	}

	function groupFieldsByRowSize(fields, property, maxSize) {
		if (fields) {
			var rows = [{
				fields: []
      }];
			var rowSize = 0;
			var colSize;
			for (var i = 0; i < fields.length; i++) {
				colSize = parseInt(fields[i][property], 10);
				if (colSize > maxSize) {
					colSize = maxSize;
					fields[i][property] = colSize;
				}

				rowSize += colSize;
				if (rowSize > maxSize || fields[i].breakline) {
					rows.push({
						fields: []
					});
					rowSize = colSize;
				}
				rows[rows.length - 1].fields.push(fields[i]);
			}
			return rows;
		}
	}

	$scope.$watchCollection('widget.fields', function() {
		if (self.widget) {
			self.widget.fieldGroups = groupFieldsByFieldGroup(self.widget.fields, self.widget.fieldGroups);
		}
	});
}

