function PopupController($scope, ScreenService, templateManager, eventEngine, $rootScope, $timeout) {
	var self = this;
	PopupControllerUtil.parent = this;
	PopupControllerUtil.$scope = $scope;
	PopupControllerUtil.$timeout = $timeout;
	PopupControllerUtil.$rootScope = $rootScope;
	$scope.$on("openPopup", PopupControllerUtil.getInstanceOnOpenPopup());
	this._documentMouseDown = function(e) {
		if ($(e.target).closest('.popup-container').length === 0 && $('.popup-container').is(':visible')) {
			$rootScope.$apply(function() {
				ScreenService.closePopup();
			});
			$(document).off('mousedown', this._documentMouseDown);
			$(document).off('touchstart', this._documentMouseDown);
		}
	};
	$scope.$on("closePopup", function(e) {
		$(document).off('mousedown', this._documentMouseDown);
		$(document).off('touchstart', this._documentMouseDown);
	});
	$scope.$on("changeContainer", function(e, widget) {
		ScreenService.closePopup();
	});

	$scope.closePopup = function() {
		ScreenService.closePopup();
	};

	$scope.actionClick = Util.buildDebounceMethod(function(action, widget, row) {
		// copia do ZeedhiControler.actionClick... refatorar// there is no problem here
		if (!action.aclReadOnly && !action.readOnly) {
			if (row) {
				widget.currentRow = row;
			}
			$scope.currentWidget = widget;
			templateManager.currentWidget = widget;
			var events = action.events;
			if (events) {
				eventEngine.bindEvents(action, events, function() {
					return widget.dataSource;
				}, function() {
					return widget.currentRow;
				}, function() {
					return widget.selectedRow;
				});
			}
			action.click();
		}
	}, 300, true);

	$scope.getIconClass = function(action, widget) {
		var classes = "";
		if (action.icon) {
			classes = action.icon;
			if (widget.popupNoBlock) {
				classes += ' icon-white';
			} else {
				classes += ' color-main';
			}
		}
		return classes;
	};
}

// FILE: js/components/Gadgets/SwipeController.js
function SwipeController($scope, ScreenService, templateManager, $rootScope, $timeout, $document) {

	var resizeSwipes = function() {
		var container = $(".zh-swipe-container");
		var swipes = container.children();
		for (var i = 0; i < $rootScope.swipes.length; i++) {
			var swipe = $(swipes[i]);
			if (i === 0) {
				// first swipe always have 100% of container
				swipe.css('width', '100%');
			} else {
				swipe.css('width', (i === swipes.length - 1 ? 75 - (swipes.length - 2) * 10 : 100 - (i * 10)) + '%');
			}

			var swipeOverlay = swipe.find('.swipe-overlay');
			swipeOverlay.css('width', window.innerWidth - swipe[0].clientWidth + 'px');
		}
		$timeout(function() {
			for (var i = 0; i < $rootScope.swipes.length; i++) {
				$rootScope.swipes[i].open = true;
			}
		});
		$(window).trigger('resize.grid');
	};

	$rootScope.swipes = [];
	$scope.$on("openSwipe", function(e, widget, closeAll, deferred, customClass) {
		if (closeAll) {
			$rootScope.swipes = [];
		}

		// $scope.widget = widget
		// if widget is not inside $rootScope.swipes, a new swipe is added
		if ($rootScope.swipes.indexOf(widget) === -1) {
			$rootScope.swipes.push(widget);

			var element = $(".zh-swipe-container");

			if (customClass) {
				element.addClass(customClass);
			} else {
				element.attr("class", "zh-swipe-container zh-swipe-right");
			}

			if (!element.hasClass("open")) {
				element.addClass("open");
			}
			$timeout(resizeSwipes);
		} else {
			// if widget is inside $rootScope.swipes then closes all swipes beyond current level
			var currentLevel = $rootScope.swipes.indexOf(widget) + 1;
			ScreenService.closeSwipe(currentLevel);
		}

		// delay the broadcast so the swipe can be build by ngRepeat
		$timeout(function() {
			// this function is used by list and grid to positioning the tip
			$rootScope.$broadcast("swipeOpened");
			deferred.resolve();
		}, 300);
	});

	$scope.$on("closeSwipe", function(e, level, deferred) {
		var element = $(".zh-swipe-container");

		if ($rootScope.swipes.length > 0) {
			// if level is not defined than only the last swipe is closed
			if (level === undefined) {
				level = $rootScope.swipes.length - 1;
			}
			// closes all swipes bigger than level
			for (var i = $rootScope.swipes.length; i > level; i--) {
				$rootScope.swipes[i - 1].open = false;
			}
			// delay the removal so the animation can be executed
			$timeout(function() {
				while ($rootScope.swipes.length > level) {
					$rootScope.swipes.pop();
				}
				if ($rootScope.swipes.length === 0 && element.hasClass("open")) {
					element.removeClass("open");
				}
				resizeSwipes();
				deferred.resolve();
			}, 300);
		} else {
			deferred.reject();
		}
	});

	$scope.$watch("templateManager.container", function() {
		// closes the swipes: 0 = all
		ScreenService.closeSwipe(0);
		templateManager.updateTemplate();
	});

	Zepto($document[0]).swipeRight(function(evt) {
		// prevent closing the swipe if scrolling a zh-tab-wrapper
		var zhTabWrapper = $(evt.target).closest(".zh-tab-wrapper")[0];
		if (zhTabWrapper && zhTabWrapper.scrollWidth > zhTabWrapper.offsetWidth) {
			return;
		}
		// prevent closing the swipe if scrolling a zh-scroller with horizontal scrolling enabled
		var zhScroller = $(evt.target).closest(".container[zh-scroller]")[0];
		if (zhScroller && zhScroller.scrollWidth > zhScroller.offsetWidth) {
			return;
		}
		if ($scope.widget) {
			$scope.widget.list();
			delete $scope.widget;
		}
		// closes the swipe: no args = topmost
		ScreenService.closeSwipe();
		templateManager.updateTemplate();
	});
}

