function KanbanController($scope) {
	var container = $('.zh-kanban-container');

	$scope.originColumn = null;
	$scope.destinationColumn = null;

	$scope.sortableOptions = {
		placeholder: "kanban-placeholder",
		connectWith: ".kanban-item-container",
		handle: '.kanban-handle',
		beforeStop: function(e, ui) {
			$scope.destinationColumn = $(ui.item).parents('.kanban-column').attr("column-id");
			if ($scope.widget.beforeMoveItem) {
				var event = $scope.widget.beforeMoveItem({
					"item": ui.item.sortable.model,
					"columns": {
						'from': $scope.originColumn,
						'to': $scope.destinationColumn
					}
				});
				if (event.data === false) {
					ui.item.sortable.cancel();
				}
			}
		},
		start: function(e, ui) {
			$scope.originColumn = $(e.target).parents('.kanban-column').attr("column-id");
			container.removeClass('nonbounce'); // prevent scroll during dragging
		},
		sort: function(e, ui) {
			if (ui.helper.offset().left < 0) {
				ui.helper.css('left', ui.helper.position().left - container.scrollLeft());
				container.scrollLeft(0);
			}
			if (ui.helper.offset().left + ui.helper[0].clientWidth > container.width()) {
				ui.helper.css('left', ui.helper.position().left + (container.find('.zh-kanban-column-container').width() - container.width() - container.scrollLeft()));
				container.scrollLeft(container.find('.zh-kanban-column-container').width() - container.width());
			}
		},
		stop: function(e, ui) {
			container.addClass('nonbounce'); // prevent scroll during dragging
			if (!ui.item.sortable.isCanceled()) {
				if ($scope.widget.afterMoveItem) {
					$scope.widget.afterMoveItem({
						"item": ui.item.sortable.model,
						"columns": {
							'from': $scope.originColumn,
							'to': $scope.destinationColumn
						}
					});
				}
			}
		}
	};

	$scope.formatDueDate = function(value) {
		var d = new Date(value);
		return d.toLocaleDateString();
	};
}

