				var tip = activeSwipe.find('.swipe-tip');
				// get list inner component to bind scroll event
				var inner = iElm.closest('.zh-list-container');

				var scrollList = function() {
					var top = iElm.offset().top - activeSwipe.offset().top;
					tip.css('top', top);
					tip.css('display', top < (inner.offset().top - activeSwipe.offset().top - 20) ? 'none' : 'block');
				};

				// unbind, if binded, the event to prevent multiples events caused by
				// multiples clicks on same list
				inner.off('scroll.list');
				inner.on('scroll.list', scrollList);
