// FILE: js/components/TimelineController.js
function TimelineController($scope, ScreenService) {
	$scope.widget.dataSource.data.sort(function(a, b) {
		return new Date(b.CREATED_AT) - new Date(a.CREATED_AT);
	});

	function processDates() {
		var today = new Date();
		var todayStr = today.toLocaleDateString();
		var yesterdayStr = new Date(today.setDate(today.getDate() - 1)).toLocaleDateString();
		$scope.widget.dataSource.data.forEach(function(item) {
			var date = new Date(item.CREATED_AT);
			var day = date.toLocaleDateString();
			if (day == todayStr) {
				item.dateStr = ScreenService.i18n('hoje');
				var minutes = (new Date() - date) / 1000 / 60;
				var hours = minutes / 60;
				if (hours < 1) {
					item.timeStr = Math.floor(minutes) + ' ' + ScreenService.i18n('minuto(s) atrás');
				} else {
					item.timeStr = Math.floor(hours) + ' ' + ScreenService.i18n('hora(s) atrás');
				}
			} else if (day == yesterdayStr) {
				item.dateStr = ScreenService.i18n('ontem');
				item.timeStr = date.toLocaleTimeString().substr(0, 5);
			} else {
				item.dateStr = moment(date).format('ddd, L');
				item.timeStr = date.toLocaleTimeString().substr(0, 5);
			}
		});
	}
	processDates();

	$scope.toggle = function(item) {
		item.closed = item.closed !== undefined ? !item.closed : true;
	};
}

