		'WidgetAfterMoveRow': "afterMoveRow",
		'WidgetBeforeNewRow': 'beforeNewRow',
		'WidgetNewRow': 'onNewRow',
		'WidgetOnEnter': 'onEnter',
		'WidgetOnEnterTab': 'onEnterTab',
		"WidgetOnSync": "onSync",
		'WidgetBeforeSelectRow': "beforeSelectRow",
		'WidgetAfterSelectRow': "afterSelectRow",
		"WidgetOnCancel": "onCancel",
		"WidgetOnSave": "onSave",
		"WidgetBeforeSelectAll": "beforeSelectAll",
		"WidgetAfterSelectAll": "afterSelectAll",
		"WidgetBeforeUnselectAll": "beforeUnselectAll",
		"WidgetAfterUnselectAll": "afterUnselectAll",
		'WidgetOnInitDataSource': 'initDataSource',
