// FILE: js/front-engine/SyncEngine.js
var SyncEngine = function(requestFactory, localStorageEngine, requestEngine, templateManager) {
	var SERVICE = "/queue/sync-queue";
	var SYNCHRONIZED_AT = "SYNCHRONIZED_AT";

	var getCurrentDate = function() {
		var date = new Date();
		var year = date.getFullYear();
		var month = ("0" + (date.getMonth() + 1)).slice(-2);
		var day = ("0" + date.getDate()).slice(-2);
		var hour = ("0" + date.getHours()).slice(-2);
		var minutes = ("0" + date.getMinutes()).slice(-2);
		var seconds = ("0" + date.getSeconds()).slice(-2);

		return day + "/" + month + "/" + year + " " + hour + ":" +
			minutes + ":" + seconds;
	};

	this.sync = function(serviceName, data, filters) {
		var widget = templateManager.currentWidget;
		var event;
		if (widget.onSync) {
			event = widget.onSync();
		}
		if (!event || !event.isDefaultPrevented()) {
			var dataSource = {
				"requestType": "DataSet",
				"dataset": data,
				"serviceName": serviceName + "/save",
				"filters": filters
			};
			var request = requestFactory.factory({
				"requestType": "dataSource",
				"dataSource": dataSource,
				"serviceName": SERVICE
			});

			requestEngine.doRequest(request);
			localStorage.setItem(SYNCHRONIZED_AT, getCurrentDate());
		}
	};

};

// FILE: js/front-engine/WorkerEngine.js
function ZhWebWorker() {

	var worker;
	var listeners = [];

	this.create = function(url) {
		worker = new Worker(url);
		return this;
	};

	this.sendData = function(data) {
		worker.postMessage(JSON.stringify(data));
		this.notify({
			"action": "messageSent",
			"data": true
		});
	};

	worker.addEventListener('message', function(e) {
		e = JSON.parse(e.data);
		this.notify({
			"action": "messageReceived",
			"data": e
		});
	}.bind(this), false);

	this.addListener = function(listener) {
		//if it isn't there already, add it
		if (listeners.indexOf(listener) === -1) {
			listeners.push(listener);
		}
	};

	this.removeListener = function(listener) {
		//find 1st matching method's position
		var index = listeners.indexOf(listener);
		//if it was found...
		if (index > -1) {
			//...remove it from the array
			listeners.splice(index, 1);
		}
	};

	this.notify = function(message) {
		listeners.forEach(function(listener) {
			if (listener[message.action]) {
				listener[message.action](message.data);
			}
		});
	};

}

var ZhWorkerFactory = {
	create: function(url) {
		return new ZhWebWorker().create(url);
	}
};

// FILE: js/front-engine/ZHPromise.js
function ZHPromise($q) {

	/**
	 *	@method buildDefer
	 *  @desc	Creates a deferred object
	 *
	 *  @param {function} onFulfill Callback to be binded to the promise's success
	 *  @param {function} onError   Callback to be binded to the promise's error
	 *								Chained to onFulfill promise, if provided, and it should catch errors
	 *								either from the promise itself and also from the onFulfill callback.
	 *
	 *  @param {function} onNotify  Callback to be binded to the promise's notify
	 *
	 *  @returns {Deferred} A deferred object that should expose resolve, reject and notify methods
	 *						and also the promise property that should be a thenable.
	 */
	this.defer = function(onFulfill, onError, onNotify) {
		var deferred = $q.defer();
		var promise = deferred.promise;

		/**
		 * Fallback for legacy clients, should be removed on next releases
		 * A deferred should not be a thenable
		 * Todo: add log info of the removal
		 */
		deferred.then = function(onFulfill, onError, onNotify) {
			return deferred.promise.then(onFulfill, onError, onNotify);
		};

		if (typeof onFulfill === 'function') {
			promise = promise.then(onFulfill, null, onNotify);
		}
		if (typeof onError === 'function') {
			promise = promise.then(null, onFulfill);
		}
		return deferred;
	};

	/**
	 *	@method promise
	 *  @desc	Creates a promise object
	 *
	 *  @param {function} onFulfill Callback to be binded to the promise's success
	 *  @param {function} onError   Callback to be binded to the promise's error
	 *								Chained to onFulfill promise, if provided, and it should catch errors
	 *								either from the promise itself and also from the onFulfill callback.
	 *
	 *  @param {function} onNotify  Callback to be binded to the promise's notify
	 *
	 *  @returns {promise} A thenable object
	 */
	this.promise = function(onFulfill, onError, onNotify) {
		var deferred = $q.defer();
		var promise = deferred.promise;

		if (typeof onFulfill === 'function') {
			promise = promise.then(onFulfill, null, onNotify);
		}
		if (typeof onError === 'function') {
			promise = promise.then(null, onFulfill);
		}
		return promise;
	};

	/**
	 *	@method promise
	 *  @desc	Creates a promise object
	 *
	 *  @param {Array} promises Promises array to create a new promise
	 *
	 *  @returns {promise}  A thenable object that will resolve as soon as all promises
	 *						in promises array gets resolved, or will get rejected if one or more
	 *						gets rejected.
	 */
	this.all = function(promises) {
		return $q.all(promises);
	};
}

