// FILE: js/front-engine/ZhLogger.js
function Logger(level) {
	// exposes the level parameter to be used to verify the debug level
	var self = this;
	self.level = level;

	/* The verification for the existance of level
	 * is done upon instance creating.
	 */
	var zeedhiLogo =
		"data:image/png;base64,";
	var logoStyle = "background: url(" + zeedhiLogo + ") black;" + "border:1px solid black;" + "padding-left:2px;" + "border-radius:3px;" +
		"line-height:20px";
	var messageStyle = "background:white; color:black";

	var levelNames = ["DEBUG", "INFO", "WARNING", "ERROR", "FATAL", "NONE"];
	var levelBackgrounds = ["#5bc0de", "#43ac6a", "#e99002", "#f04124", "#444"];
	var levelType = ["log", "log", "warn", "error", "error"];

	/* This is meant for on-the-fly switching.
	 * Meant to be set by the constructor initially.
	 */
	this.setLevel = function(levelSetter) {
		self.level = isNaN(levelSetter) ? levelNames.indexOf(levelSetter) : levelSetter;
		return self.level;
	};

	var log = function(requestLevel, message, objectArray) {
		if (self.level <= requestLevel) {

			var header = "%c      %c " + levelNames[requestLevel] + " %c ";
			var levelStyle = "background:" + levelBackgrounds[requestLevel] + ";color:white";

			console[levelType[requestLevel]](header + message, logoStyle, levelStyle, messageStyle);

			if (Array.isArray(objectArray)) {
				objectArray.forEach(function(object) {
					console.log(object);
				});
			} else if (typeof objectArray === "object" || objectArray instanceof Object === true) {
				console.log(objectArray);
			}

		}
	};

	this.debug = function(message, objectArray) {

		log(0, message, objectArray);

	};

	this.info = function(message, objectArray) {

		log(1, message, objectArray);

	};

	this.warning = function(message, objectArray) {

		log(2, message, objectArray);

	};

	this.error = function(message, objectArray) {

		log(3, message, objectArray);

	};

	this.fatal = function(message, objectArray) {

		log(4, message, objectArray);

	};

	this.table = function(objectArray, title, index) {

		if (title) {
			console.log("[ ZEEDHI TABLE ] " + title);
		}

		if (Array.isArray(objectArray)) {
			if (index) {
				console.table(index, objectArray);
			} else {
				console.table(objectArray);
			}
		} else if (typeof objectArray === "object" || objectArray instanceof Object === true) {
			if (index) {
				console.table(index, [objectArray]);
			} else {
				console.table([objectArray]);
			}
		}

	};

}

