// FILE: js/render-engine/Models/ZhUtil.js
var ZhUtil = function(ApplicationContext) {
	this.evaluate = function($string) {
		var $a, $function_detector, $function_new, $ret, $string_ret, _i, _len;
		if (angular.isFunction($string)) {
			$string_ret = $string();
		} else {
			$string_ret = $string;
			$function_detector = /\@(.*?)\@/g;
			if ($string_ret) {
				$ret = $string_ret.toString().match($function_detector);
				if ($ret) {
					var keys = Object.keys(ApplicationContext);
					var args = keys.map(function(key) {
						return ApplicationContext[key];
					});
					var argsToFunction = ApplicationContext.getArgsToFunction();
					for (_i = 0, _len = $ret.length; _i < _len; _i++) {
						$a = $ret[_i];
						$function_new = $a.replace(/\@/g, '');
						/*jslint evil: true */
						eval('$return = (( function(' + argsToFunction.keys.join(' , ') + '){ return ' +
							$function_new +
							';} ).bind(ApplicationContext) ).apply(ApplicationContext, argsToFunction.args);');
						$string_ret = $string_ret.replace($a, $return);
					}
				}
			}
		}
		return $string_ret;
	};
};

