	$scope.addHistoryItem = function(containerName) {
		if (templateManager.historyItems) {
			var idx = templateManager.historyItems.indexOf(containerName);
			if (idx > -1) {
				// remove the item from historyItems to prevent duplicates
				templateManager.historyItems.splice(idx, 1);
			}
			templateManager.historyItems.push(containerName);
		}
	};
