ZeedhiChart.directive('zhGauge', function($timeout, maskEngine) {
	return {
		restrict: 'A',
		scope: {
			data: '=',
			options: '='
		},
		link: function(scope, ele, attrs) {

			var getWidget = function() {
				return scope.$parent.widget;
			};

			var canvas = ele.find('canvas');
			var text = ele.find('span');
			var opts = {
				lines: 20,
				angle: 0,
				lineWidth: 0.45,
				limitMax: true,
				colorStart: '#666666',
				strokeColor: '#eeeeee',
				highDpiSupport: false,
				levels: getWidget().gaugeLevels,
				pointer: {
					length: 0.70,
					strokeWidth: 0.035,
					color: '#333333'
				}
			};

			function setValueText(value) {
				if (getWidget().valueFormat && maskEngine[getWidget().valueFormat + "Format"]) {
					var formatedValue = maskEngine[getWidget().valueFormat + "Format"](value, gauge.maxValue, gauge.minValue);
					getWidget().valueText = formatedValue;
				} else {
					getWidget().valueText = value;
				}
			}

			function setHintText(value) {
				if (getWidget().hintFormat) {
					if (maskEngine[getWidget().hintFormat + "Format"]) {
						// If the given format is a valid maskEngine format uses it to format the values
						var formatedValue = maskEngine[getWidget().hintFormat + "Format"](value, gauge.maxValue, gauge.minValue);
						var formatedMaxValue = maskEngine[getWidget().hintFormat + "Format"](gauge.maxValue);
						getWidget().hintText = "(" + formatedValue + "/" + formatedMaxValue + ")";
					} else {
						// @TODO: Evaluate the format function and uses the returned value as hintText
						// var formatedText = ZhUtil.evaluate(getWidget().hintFormat);
						// getWidget().hintText = formatedText;
					}
				} else {
					getWidget().hintText = "(" + value + "/" + gauge.maxValue + ")";
				}
			}

			function setGaugeValue(value) {
				var validValue;

				if (value >= gauge.maxValue) {
					validValue = gauge.maxValue;
				} else if (value <= gauge.minValue) {
					validValue = gauge.minValue + 1;
				} else {
					validValue = value;
				}

				gauge.set(validValue);
				setValueText(validValue);
				setHintText(validValue);
			}

			ele.click(function() {
				getWidget().setCurrentRow(getWidget().dataSource.data[0]);
			});

			function setTextSize() {
				text.css('font-size', Math.min(ele.innerWidth() * 0.075, ele.innerHeight() * 0.1) + 'px');
			}

			function setCanvasSize() {
				canvas.attr('width', ele.innerWidth());
				canvas.attr('height', ele.innerHeight() * 0.9);
			}

			function drawGauge() {
				setTextSize();
				setCanvasSize();
				gauge.setOptions(opts);
				gauge.update(true);
			}

			gauge = new Gauge(canvas[0]);
			gauge.minValue = getWidget().minValue || 0;
			gauge.maxValue = getWidget().maxValue;
			gauge.animationSpeed = 24;
			$timeout(function() {
				drawGauge();
			}, 200);

			if (getWidget().dataSource && getWidget().dataSource.data && getWidget().dataSource.data[0]) {
				gauge.value = getWidget().dataSource.data[0][getWidget().valueField];
				setGaugeValue(gauge.value);
			} else {
				gauge.value = gauge.minValue;
				setGaugeValue(gauge.value);
			}

			getWidget().redraw = function() {
				drawGauge();
			};

			getWidget().restartGauge = function() {
				canvas.remove();
				canvas = $("<canvas id='novo'></canvas>");
				ele.prepend(canvas);

				gauge = new Gauge(canvas[0]);
				gauge.minValue = getWidget().minValue || 0;
				gauge.maxValue = getWidget().maxValue;
				gauge.animationSpeed = 24;

				opts.levels = getWidget().gaugeLevels;
				drawGauge();
			};

			$(window).resize(function() {
				if (ele.is(":visible")) {
					drawGauge();
				}
			});

			scope.$parent.$watch('widget.dataSource.data[0]["' + getWidget().valueField + '"]', function(e) {
				setTextSize();
				setCanvasSize();
				gauge.value = getWidget().dataSource.data[0][getWidget().valueField];
				setGaugeValue(gauge.value);
			});
		}
	};
});


