ZeedhiEvents.directive('zhWidgetEvents', function(ZhLogger, templateManager, eventEngine, $rootScope) {

	function bindWidgetEvents(ZhLogger, widget, isFilter) {

		if (!widget) {
			ZhLogger.error("No widget or filter on scope.");
			return;
		}
		if (widget.onEnter) {
			widget.onEnter();
		}

		if (widget.filter && !isFilter) {
			bindWidgetEvents(ZhLogger, widget.filter, true);
		} else if (widget.filter && isFilter) {
			ZhLogger.error("A filter should never have a filter.");
		}
	}

	return function preLink($scope, element, attrs) {

		var widget = attrs.zhWidgetEvents ? $scope.$eval(attrs.zhWidgetEvents) : $scope.widget ? $scope.widget : $rootScope._currentFilter;

		bindWidgetEvents(ZhLogger, widget);

	};
});

ZeedhiEvents.directive('zhEvents', function(templateManager, eventEngine) {
	return function preLink($scope, element, attrs) {
		var field = attrs.zhEvents ? $scope.$eval(attrs.zhEvents) : $scope.field;
		var events = field.events;

		var businessEventsAttrs = attrs.businessEvents ? attrs.businessEvents.split('|') : [];
		var businessEvents = [];

		if (businessEventsAttrs.length) {
			events = events.filter(function(event) {
				var isBusinessEvent = businessEventsAttrs.indexOf(event.name) !== -1;
				if (isBusinessEvent) {
					businessEvents.push(event);
				}
				return !isBusinessEvent;
			});
		}

		var widget = field.widget;
		element.field = field;
		element.getParent = function() {
			return field;
		};

		var bindEvents = function(owner, events) {
			eventEngine.bindEvents(owner, events,
				function() {
					if (widget.dataSource) {
						return widget.dataSource.data;
					}
				},
				function() {
					return widget.currentRow;
				},
				function() {
					return widget.selectedRow;
				}
			);
		};

		if (events && events.length) {
			bindEvents(element, events);
		}
		if (businessEvents && businessEvents.length) {
			bindEvents(element.field, businessEvents);
		}
	};
});

