ZeedhiEvents.directive('zhPushValue', function(templateManager) {
	return function preLink($scope, element, attrs) {
		var execDirective = function() {
			if (!element.field.aclReadOnly && !element.field.readOnly) {
				var value = $scope.item[$scope.field.name] || $scope.$eval(attrs.ngBind || attrs.ngModel);
				var fieldName = $scope.$eval(attrs.zhPushValue);
				var source = $scope.widget;
				var removed;
				source.currentRow[fieldName] = source.currentRow[fieldName] || [];
				if (angular.isArray(source.currentRow[fieldName])) {
					for (var i = 0; i < source.currentRow[fieldName].length; i++) {
						if (source.currentRow[fieldName][i] === value) {
							source.currentRow[fieldName].splice(i, 1);
							removed = true;
							element.change();
						}
					}
					if (!removed) {
						source.currentRow[fieldName].push(value);
						element.change();
					}
				} else {
					if (source.currentRow[fieldName].indexOf(value) === -1) {
						source.currentRow[fieldName] += value + "; ";
					} else {
						source.currentRow[fieldName] = source.currentRow[fieldName].replace(value + "; ", "");
					}
				}
			}

		};

		element.on('touchstart', execDirective);
	};
});

