ZeedhiFields.directive("zhInputNumber", function(templateManager) {
	return {
		require: 'ngModel',
		link: function($scope, element, attrs, controller) {
			var divContainer = element.parent().parent();
			var minusButton = divContainer.children().eq(0);
			var plusButton = divContainer.children().eq(2);
			var input = element;

			var minValue = parseFloat($scope.field.minValue, 10);
			var maxValue = parseFloat($scope.field.maxValue, 10);
			minValue = isNaN(minValue) ? -Number.MAX_VALUE : minValue;
			maxValue = isNaN(maxValue) ? Number.MAX_VALUE : maxValue;

			var currentVal;

			$scope.buttonClick = function(plus) {
				var val = parseInt($scope.fieldRow, 10);
				if(isNaN(val)){
					val = 0;
				}

				if (!plus) {
					if (val > minValue) {
						input.val(val - 1).change();
					}
				} else {
					if (val < maxValue) {
						input.val(val + 1).change();
					}
				}

				input.change();
			};

			function inputChange() {
				var val = parseFloat(input.val(), 10);
				if (!isNaN(val) && (val >= minValue && val <= maxValue)) {
					currentVal = val;
				}
				input.val(currentVal);
				if (currentVal === minValue) {
					minusButton.attr('disabled', true);
				} else {
					minusButton.attr('disabled', false);
				}
				if (currentVal === maxValue) {
					plusButton.attr('disabled', true);
				} else {
					plusButton.attr('disabled', false);
				}
			}

			input.on('change', inputChange);
			var value = parseInt($scope.field.value(), 10);
			$scope.fieldRow = value;
			input.change();

			controller.$formatters.unshift(function(value) {
				input.val(value).change();
				return value;
			});
		}
	};
});

ZeedhiFields.directive("zhIcon", function(ZhSprite, ZhLogger) {
	return {
		restrict: 'A',
		link: function(scope, element, attrs) {

			function getSpriteUrl() {
				var bkgImageUrl = ZhSprite.getSpriteUrl();
				if (!bkgImageUrl) {
					bkgImageUrl = element.css('backgroundImage') || window.getComputedStyle(element[0], false).backgroundImage;
					if (bkgImageUrl !== "") {
						// Removes 'url(', ')' and possible quottation marks
						bkgImageUrl = bkgImageUrl.slice(4, -1);
						if (bkgImageUrl[0] == '"') {
							bkgImageUrl = bkgImageUrl.slice(1, -1);
						}
					} else if (element.bkgImageUrl) {
						bkgImageUrl = element.bkgImageUrl;
					}
					ZhSprite.setSpriteUrl(bkgImageUrl);
				}
				return bkgImageUrl;
			}

			function injectSVG(iconName) {
				var icon = ZhSprite.getIcon(iconName);

				if (!icon) {
					ZhLogger.warning('Icon "' + iconName + '" not found on sprite!');
				} else {
					var svgElement = ZhSprite.getSprite().querySelector('svg');
					var svgChild = angular.element('<svg width="' + svgElement.getAttribute('width') + '" height="' + svgElement.getAttribute('height') + '"></svg>');
					svgChild.append($(icon).clone());

					if (!element.hasClass('zh-icon-no-border')) {
						var svgCircle = ZhSprite.getIcon("border");
						svgChild.append($(svgCircle).clone());
					}
					element.append(svgChild);
				}

				var displayValue = 'block';
				if (element.hasClass("zh-icon-inline")) {
					displayValue = 'inline';
				} else if (element.hasClass("zh-icon-inline-block")) {
					displayValue = 'inline-block';
				}
				element.css('display', displayValue);
			}

			function clearIconAndStyles(element) {
				if (element.hasIcon || element.children().length) {
					element.attr("style", "");
					element.empty();
					element.hasIcon = false;
				}
			}

			function clearClasses(element) {
				var classList = element[0].classList.length ? element[0].classList.toString().split(" ") : [];

				if (classList.length) {
					classList.forEach(function(str) {
						if (str.substr(0, 7) === "zh-icon") {
							element.removeClass(str);
						}
					});
				}
			}

			function processIcon() {
				var iconString = attrs.zhIcon;
				var iconName, otherClasses = "";

				clearClasses(element);
				// Filters the icon string in search of the icon name
				iconString.split(" ").forEach(function(classStr) {
					switch (classStr) {
						case "icon-black":
							otherClasses += " zh-icon-color-black";
							break;
						case "icon-white":
							otherClasses += " zh-icon-color-white";
							break;
						case "icon-main":
							otherClasses += " zh-icon-color-main";
							break;
						case "no-border":
							otherClasses += " zh-icon-no-border";
							break;
						default:
							iconName = classStr;
					}
				});

				if (iconName) {
					element.addClass('zh-icon zh-icon-' + iconName + otherClasses);
					clearIconAndStyles(element);

					var spriteUrl = getSpriteUrl();
					if (spriteUrl) {
						element.hasIcon = true;
						element.css('backgroundImage', 'none');
						element.bkgImageUrl = spriteUrl;
						ZhSprite.loadSprite(spriteUrl).then(function() {
							injectSVG(iconName);
						});
					}
				}
			}

			attrs.$observe("zhIcon", function(newValue, oldValue) {
				if (newValue !== oldValue) {
					processIcon();
				}
			});
		}
	};
});

ZeedhiFields.directive("zhNumberKeyboard", function($document, templateManager) {

	return function(scope, element, attrs) {

		var input = element.find("input");
		var keyboard = element.find(".zh-number-keyboard");
		var label = element.find("label");
		var currentVal;
		var minValue = parseFloat(scope.field.minValue, 10);
		var maxValue = parseFloat(scope.field.maxValue, 10);
		minValue = isNaN(minValue) ? -Number.MAX_VALUE : minValue;
		maxValue = isNaN(maxValue) ? Number.MAX_VALUE : maxValue;

		if(scope.field.maxlength){
			maxValue = "0";
			for(var i = 0; i< scope.field.maxlength; i++ ){
				maxValue += "9";
			}
			maxValue = parseInt(maxValue);
		}

		var hasMask = function(field) {
			return scope.field.mask && scope.field.mask.type === "fix";
		};

		var otherMask = function(field){
			return !!scope.field.mask;
		};

		scope.increase = function(val) {
			var result = scope.fieldRow ? scope.fieldRow.toString() : "";
			if(hasMask(scope.field) || !scope.field.maxlength || scope.field.maxlength > result.length){
				if (Util.isUndefinedOrNull(result)) {
					result = "";
				}
				result += val;

				if (hasMask(scope.field)){
					result = Util.applyMask(result, scope.field.mask.params.mask);
				} else {
					result = parseFloat(result, 10);
				}
				scope.fieldRow = result;
			}
		};

		var toggleSignalFlow = {
			string: function(value){
				if (value[0] === "-") {
					value = value.slice(1);
				} else {
					value = "-" + value;
				}
				return value;
			},
			number: function(value){
				return value * -1;
			}
		};

		scope.toggleSignal = function() {
			if(toggleSignalFlow[typeof scope.fieldRow]){
				scope.fieldRow = toggleSignalFlow[typeof scope.fieldRow](scope.fieldRow);
			}
		};

		scope.backspace = function() {
			if (scope.fieldRow !== null) {
				var number = scope.fieldRow.toString().slice(0, -1);
				scope.fieldRow = number ? parseFloat(number) : "";
			}
		};

		scope.clearField = function() {
			scope.fieldRow = "";
		};

		scope.confirm = function() {
			keyboard.toggle();
			input.blur();
			if (scope.field.onConfirm) {
				scope.field.onConfirm();
			}
		};

		function getLocaleSeparator() {
			var num = 1.1;
			return num.toLocaleString().slice(1, -1);
		}
		scope.localeSeparator = scope.field.separator || getLocaleSeparator();

		input.focus(function() {
			input.blur();
		});

		input.click(function(evt) {
			evt.stopPropagation();

			$(".zh-number-keyboard:visible").hide().parent().find('input').blur();
			keyboard.toggle();

			var keys = $.makeArray(keyboard.find("td"));
			var keySize = parseInt(window.getComputedStyle(keyboard[0]).width, 10) / 4;
			var inputHeight = parseInt(window.getComputedStyle(input[0]).height, 10);
			var inputWidth = parseInt(window.getComputedStyle(input[0]).width, 10);
			var arrowHeight = 10;
			var labelHeight = 0;

			if (label) {
				var h = parseInt(window.getComputedStyle(label[0]).height, 10);
				if (!isNaN(h)) {
					labelHeight = h;
				}
			}

			// Altura do teclado = input + seta + 4 tr's
			var keyboardHeight = inputHeight + arrowHeight + (keySize * 5);

			// Ajusta o keySize de acordo com o ambiente mobile ou desktop
			if (keyboardHeight > window.innerHeight) {
				keySize = (window.innerHeight - inputHeight - arrowHeight) / 4 - 10;
			}

			for (var i = 0; i < keys.length; i++) {
				keys[i].style.width = (keySize) + "px";
				keys[i].style.height = (keySize) + "px";
			}

			// Posiciona verticalmente o teclado de acordo com o inputHeight e a altura da seta
			keyboard[0].style.top = inputHeight + arrowHeight + labelHeight + 5 + "px";

			var left = (inputWidth - parseInt(window.getComputedStyle(keyboard[0]).width, 10)) / 2 ;
			if(left < 0) {
				left = 0;
				keyboard[0].classList.add('left-pos-arrow');
			}

			keyboard[0].style.left = left + "px";

			// Evita que to teclado apareça quando o input está ativado
			input.blur();

			var keyboardOffset = keyboard.offset();
			var scroller = keyboard.closest("[zh-scroller]");
			var top = keyboardOffset.top - scroller.offset().top;

			if(window.innerHeight - input.offset().top + inputHeight < keyboardHeight) {
				scroller.animate({
					scrollTop: top + inputHeight
				}, 500);
			}

		});

		$document.click(function() {
			if(keyboard.is(':visible')){
				keyboard.hide();
				input.blur();
			}
		});

		scope.buttonClick = function(plus) {
			if(!hasMask(scope.field)){
				var val = parseInt(scope.fieldRow, 10);
				if(isNaN(val)){
					val = 0;
				}

				if (!plus) {
					if (val > minValue) {
						input.val(val - 1).change();
					}
				} else {
					if (val < maxValue) {
						input.val(val + 1).change();
					}
				}

				input.change();
			}
		};

		function inputChange() {
			if(!hasMask(scope.field) && !otherMask(scope.field)){
				var minusButton = element.find('.spin-button-minus');
				var plusButton = element.find('.spin-button-plus');
				var val = parseFloat(input.val(), 10);
				if (!isNaN(val) && (val >= minValue && val <= maxValue)) {
					currentVal = val;
				}
				input.val(currentVal);
				scope.fieldRow = currentVal;
				if (currentVal === minValue) {
					minusButton.attr('disabled', true);
				} else {
					minusButton.attr('disabled', false);
				}
				if (currentVal === maxValue) {
					plusButton.attr('disabled', true);
				} else {
					plusButton.attr('disabled', false);
				}
			}
		}

		input.on('change', inputChange);

		input.on('blur', function(){
			input.trigger('change');
		});

	};
});

ZeedhiFields.directive("zhInputPhone", function(templateManager) {
	return {
		require: 'ngModel',
		link: function($scope, element, attrs, controller) {
			var extraKeys = [13, 33, 34, 35, 36, 37, 38, 39, 40, 46, 116];
			var cleanKeys = [8, 9];

			var valueChange = function(e) {
				var text = element.val();
				if ((e.keyCode >= 48 && e.keyCode <= 57) || (e.keyCode >= 96 && e.keyCode <= 105) || cleanKeys.indexOf(e.keyCode) !== -1) {
					text = text.replace(/[^\d]/g, '');
					if (text.length > 0) {
						text = "(" + text;
					}
					if (text.length > 2) {
						text = [text.slice(0, 3), ") ", text.slice(3)].join('');
					}
					if (text.length === 5) {
						text = text.slice(0, -2);
					}
					if (text.length > 9 && text.length < 14) {
						text = [text.slice(0, 9), "-", text.slice(9)].join('');
					} else if (text.length >= 14) {
						text = [text.slice(0, 10), "-", text.slice(10)].join('');
					}
					if (text.length > 15) {
						text = text.slice(0, 15);
					}
					element.val(text);
				} else if (extraKeys.indexOf(e.keyCode) === -1) {
					text = text.slice(0, -1);
				}
				element.val(text);
			};

			element.bind('keyup', valueChange);

			var oldValue = "";
			var changed = false;

			element.bind('change', function() {
				changed = true;
			});
			element.bind('focus', function() {
				oldValue = element.val();
				changed = false;
			});
			element.bind('blur', function() {
				if (oldValue !== element.val() && !changed) {
					element.trigger('change');
				}
			});
		}
	};
});

ZeedhiFields.directive('zhSelectMultiple', function($compile, $timeout, templateManager) {
	return {
		link: function($scope, element) {
			// these options must be replaced by dataSource values
			var options = [];

			var vField = $scope.field.valueField;
			var dField = $scope.field.descriptionField;

			$scope.blurInput = function() {
				element.blur();
			};

			$scope.removeOption = function(elm, evt) {
				if (!$scope.field.readOnly) {
					options.splice(elm.index(), 1);
					$scope.fieldRow.splice(elm.index(), 1);
					elm.animate({
						opacity: 0
					}, 300, function() {
						elm.animate({
							width: 0
						}, 300, buildOptions);
					});
					$scope.field.widget.currentRow.changed = true;
					if ($scope.field.change) {
						$scope.field.change();
					}
				}
				evt.stopPropagation();
			};

			function buildOptions() {
				var container = element.find('.zh-options-container');
				var elementWidth = element.width();
				var optionsWidth = 0;

				// clear options added to element
				container.html('');

				// add options to element until options width be greater than element width
				options.forEach(function(item, index) {
					// descricao item[$scope.field.descriptionField]
					var option;
					if ($scope.field.readOnly || $scope.field.aclReadOnly) {
						option = $compile('<span class="zh-select-item"><span>&times;</span>' + item + '</span>')($scope);
					} else {
						option = $compile('<span class="zh-select-item" zh-touchstart="removeOption(elm, evt)"><span>&times;</span>' + item + '</span>')($scope);
					}
					container.append(option);
					optionsWidth += option[0].clientWidth + 5;
					if (optionsWidth > elementWidth) {
						return false;
					}
				});

				// adjust the options and add the "more" element
				if (optionsWidth > elementWidth) {
					var more = $compile('<span class="zh-select-item more-items">' + (options.length - container.children().length) + ' mais...</span>')($scope);
					container.append(more);
					optionsWidth += more[0].clientWidth + 5;
					while (optionsWidth > elementWidth && container.children().length > 1) {
						var lastOption = container.children().eq(-2);
						optionsWidth -= lastOption[0].clientWidth + 5;
						lastOption.remove();
						more.html((options.length - container.children().length + 1) + ' mais...');
					}
				}
			}

			// bind the window.resize event to rebuild the options if element width changes
			$(window).on("resize.select-multiple", buildOptions);

			// built options property checking the values (selectedRows) against the datasource
			function buildOptionsFromFieldValue(selectedRows) {
				options = [];
				if (selectedRows) {
					selectedRows.forEach(function(valueItem) {
						var matchRows = $scope.field.dataSource.data.filter(function(item) {
							return item[vField] == valueItem;
						});
						var desc = matchRows.length > 0 ? matchRows[0][dField] : null;
						if (desc && options.indexOf(desc) == -1) {
							options.push(desc);
						}
					});
				}
			}

			$scope.$watch(function() {
				return $scope.field.value();
			}, function(value) {
				buildOptionsFromFieldValue(value);
				buildOptions();
			});
		}
	};
});

