ZeedhiFields.directive('zhCheckbox', function(templateManager) {
	return {
		link: function($scope, elem) {
			var field = $scope.field;
			var input = elem.find("input");
			var label = elem.find("label");

			field.valueToTrue = field.valueToTrue || "Y";
			field.valueToFalse = field.valueToFalse || "N";

			function toggleValue() {
				field.checked = !field.checked;
				if (field.checked) {
					$scope.widget.currentRow[field.name] = field.valueToTrue;
				} else {
					$scope.widget.currentRow[field.name] = field.valueToFalse;
				}

				if (field.change) {
					field.change();
				}

				templateManager.onUpdate();
			}

			if ($scope.widget.currentRow[field.name] == field.valueToTrue) {
				field.checked = true;
			} else if ($scope.widget.currentRow[field.name] == field.valueToFalse) {
				field.checked = false;
			} else if (field.checked) {
				$scope.widget.currentRow[field.name] = field.valueToTrue;
			}

			if (!field.readOnly && !input.is(':disabled')) {
				label.click(toggleValue);
				label.find(".zh-box").keypress(function(e) {
					e.preventDefault();
					toggleValue();
				});
			}
		}
	};
});

