ZeedhiDirectives.directive('zhValidate', function(templateManager, validateEngine) {
	return {
		require: '?ngModel',
		link: function($scope, element, attrs, controller) {
			$scope.field.validate = function(value) {
				var widget = $scope.widget ? $scope.widget : $scope.__filter();
				var validations = $scope.field.validations;

				if ($scope.field.isVisible && $scope.field.aclIsVisible) {
					if (validations) {
						var fieldName = $scope.field.name;
						if (!widget.currentRow.$error) {
							widget.currentRow.$error = {};
						}
						widget.currentRow.$error[fieldName] = [];
						for (var validation in validations) {
							if (validations.hasOwnProperty(validation)) {
								var validateResult = validateEngine[validation](value, validations[validation]);
								if (!validateResult.valid) {
									widget.currentRow.$error[fieldName].push(validateResult);
								}
							}
						}
						if (widget.currentRow.$error[fieldName].length === 0) {
							return value;
						}
					}
					widget.currentRow.changed = true;
				}
				return value;
			};
			controller.$parsers.unshift(function(value) {
				return $scope.field.validate(value);
			});
		}
	};
});

