ZeedhiDirectives.directive("zhDateTime", function(ZhUtil, ScreenService) {
	/*
	 *   Date Time diective for wheel selector. Defaults to a date + time scroller.
	 *   Accepts the parameters described in opt. Consult docs to see what values they accept.
	 */
	return {
		restrict: 'A',
		link: function(scope, element, attrs, ctrl) {
			var field = scope.field || scope.question;

			function change() {
				var lang = field['datetime-lang'];
				var maxDate = ZhUtil.evaluate(field['datetime-maxDate'] || '2100-01-01 23:59');
				var minDate = ZhUtil.evaluate(field['datetime-minDate'] || '1900-01-01 00:00');
				var defaultDate = ZhUtil.evaluate(field['datetime-defaultDate'] || "today");

				if (defaultDate === "today") {
					defaultDate = new Date();
				} else {
					defaultDate = new Date(defaultDate);
				}

				if (maxDate === "today") {
					maxDate = new Date();
				} else {
					maxDate = new Date(maxDate);
				}

				if (minDate === "today") {
					minDate = new Date();
				} else {
					minDate = new Date(minDate);
				}

				var opt = {
					preset: field['datetime-preset'] || 'datetime',
					dateOrder: field['datetime-dateOrder'],
					timeWheels: field['datetime-timeWheels'],
					defaultValue: defaultDate,
					minDate: minDate,
					maxDate: maxDate,
					stepMinute: field['datetime-stepMinute'] || 1,
					theme: 'default',
					mode: 'scroller',
					lang: lang ? (lang === 'en-US' ? '' : lang) : 'pt-BR',
					hourText: "Hours",
					minuteText: "Minutes",
					secText: "Seconds",
					display: field['datetime-display'] || 'bottom',
					animate: field['datetime-animate'] || '',
					headerText: ScreenService.i18n(field.label) || ''
				};
				element.mobiscroll(opt);
			}
			element.focus(change);
			angular.element(document).ready(change);
			scope.$watch('field', change);
		}
	};
});

ZeedhiDirectives.directive("zhBetweenDates", function(ZhUtil, ScreenService, templateManager){
	return {
		restrict: 'A',
		link: function($scope, element, attrs, ctrl) {
			var field = $scope.field;
			var startField = element.find("input:first");
			var endField = element.find("input:last");
			var errorObj = { message: ScreenService.i18n('The first date must be lower than second.') };

			var addErrorMessage = function(field){
				if(!field.widget.currentRow.$error){
					field.widget.currentRow.$error = {};
				}
				if(!field.widget.currentRow.$error[field.name]){
				 field.widget.currentRow.$error[field.name] = [];
				}
				var notExists = true;
				for(var i in field.widget.currentRow.$error[field.name]){
					if(field.widget.currentRow.$error[field.name][i] === errorObj){
						notExists = false;
						break;
					}
				}
				if(notExists){
					field.widget.currentRow.$error[field.name].push(errorObj);
				}
			};

			var removeErrorMessage = function(field){
				if(field.widget.currentRow.$error && field.widget.currentRow.$error[field.name]){
					for(var i in field.widget.currentRow.$error[field.name]){
						if(field.widget.currentRow.$error[field.name][i] === errorObj){
							field.widget.currentRow.$error[field.name].splice(i, 1);
						}
					}
				}
			};

			var change = function(e){
				this.blur();

				var rowValue = {
					start: startField.val(),
					end: endField.val()
				};

				if(!field.widget.currentRow){
					field.widget.currentRow = {};
				}
				field.widget.currentRow[field.name] = rowValue;

				var startDate = rowValue.start.split('/').reverse().join('-');
				var endDate = rowValue.end.split('/').reverse().join('-');

				if(moment(new Date(startDate)).isAfter(new Date(endDate))){
					addErrorMessage(field);
				} else {
					removeErrorMessage(field);
				}

				if(field.change){
					field.change();
				}
				templateManager.updateTemplate();
			};

			startField.focus(change);
			endField.focus(change);
		}
	};
});

ZeedhiDirectives.directive("zhTabWrapper", function(templateManager) {

	return {
		restrict: 'C',
		scope: {
			tabWidget: '=',
			tabWidgets: '='
		},
		link: function(scope, elm, attrs) {

			var element = elm[0];

			var scroll = new IScroll(element, {
				eventPassthrough: true,
				scrollX: true,
				scrollY: false,
				preventDefault: false
			});
			templateManager.scrollers = templateManager.scrollers || [];
			templateManager.scrollers.push(scroll);

			var object = {
				firstRefresh: 3000,
				regularRefresh: 1000,
				interval: 100,
				clearScheduler: function() {
					clearTimeout(object.timer);
					object.currentTime = 0;
					// when it's the second time running decreases the refresh rate
					// from object.firstRefresh to object.regularRefresh
					object.refresh = object.firstRun ? object.regularRefresh : object.firstRefresh;
				},
				startUpdate: function() {
					// if the tabWidget or tabWidgets cease to exist, do not set schedule.
					// this happens when a tabbedWidget is inside a tabbed container
					// and the container changes tabs.
					object.clearScheduler();
					if (scope.tabWidget && scope.tabWidgets) {
						object.setScheduler();
					}
				},
				setScheduler: function() {
					// checks if it should refresh again, and if so, increments the timer.
					// otherwise, time's reset and no other event is scheduled.
					// redundant verification for the existance of the tabWidget and tabWidgets objects.
					if ((object.currentTime < object.refresh) && (scope.tabWidget && scope.tabWidgets)) {
						object.udpateElements();
						object.timer = setTimeout(object.setScheduler, object.interval);
						object.currentTime += object.interval;
					} else {
						object.firstRun = true;
					}
				},
				udpateElements: function() {
					// does simple math to decide the size of the line and its position.
					object.elementDiv = object.elementDiv || $($(element).find('div')[0]);
					object.elementLine = object.elementLine || object.elementDiv.find('.zh-tab-line');
					object.elementCell = object.elementDiv.find('.zh-tab-cell');

					var index = scope.tabWidgets.filter(function(widget){
						return widget.isVisible && widget.aclIsVisible && widget.showAsTab;
					}).indexOf(scope.tabWidget);
					var elementWidth = $(object.elementCell[index]).width(),
						priorWidth = object.getPriorWidth(index);

					object.elementLine.width(elementWidth);
					object.elementLine.css('left', priorWidth);
				},
				getPriorWidth: function(index) {
					// gets the index of a tab and returns the width of its combined predecessors
					var priorWidth = 0;
					for (i = 0; i < index; i++) {
						priorWidth += $(object.elementCell[i]).width();
					}
					return index ? priorWidth + (index * 15) : priorWidth;
				}
			};

			['tabWidget', 'tabWidgets'].forEach(function(each) {
				scope.$watch(each, object.startUpdate);
			});
		}
	};

});

