ZeedhiDirectives.directive('zhMasonryGrid', function($timeout) {
	function _matrix(numRows, numCols, initial) {
		var arr = [];
		for (var y = 0; y < numRows; y++) {
			var cols = [];
			for (var x = 0; x < numCols; x++) {
				cols.push(initial);
			}
			arr.push(cols);
		}
		return arr;
	}

	function _growMatrix(arr, numRows, numCols, initial) {
		for (var y = 0; y < numRows; y++) {
			var cols = [];
			for (var x = 0; x < numCols; x++) {
				cols.push(initial);
			}
			arr.push(cols);
		}
		return arr;
	}

	function _generatePosition(matrix, numCols, width, height) {
		var foundSpot = false;
		var pos = {};
		var x, y, j, k;
		var rows = matrix.length;
		for (y = 0; y < rows; y++) {
			for (x = 0; x < numCols; x++) {
				if (matrix[y][x] === 0) { // empty space
					var allGood = true;
					if (x + width > numCols || y + height > rows) {
						allGood = false;
					}
					if (allGood) {
						for (j = 0; j < width; j++) {
							for (k = 0; k < height; k++) {
								if (matrix[y + k][x + j] !== 0) {
									allGood = false;
									break;
								}
							}
							if (!allGood) {
								break;
							}
						}
					}
					if (allGood) {
						foundSpot = true;
						pos.top = y;
						pos.left = x;
						for (j = 0; j < width; j++) {
							for (k = 0; k < height; k++) {
								matrix[y + k][x + j] = 1;
							}
						}
						break;
					}
				}
			}
			if (foundSpot) {
				break;
			}
		}

		if (foundSpot) {
			return pos;
		} else {
			_growMatrix(matrix, height, numCols, 0);
			return _generatePosition(matrix, numCols, width, height);
		}
	}

	function hasAttr(element, attr) {
		return (typeof element.attr(attr) !== "undefined" && element.attr(attr) !== false) || (typeof element.attr('data-' + attr) !== "undefined" &&
			element.attr('data-' + attr) !== false);
	}

	var masonryGrids = [];
	$(window).on('resize.masonry', function() {
		masonryGrids.forEach(function(grid) {
			if (grid.is(':visible') && grid.rendered) {
				var newCols = grid.getColumns();
				if (newCols != grid.columns) {
					grid.columns = newCols;
					grid.render();
				} else {
					grid.positionGridItems();
				}
			}
		});
	});

	return {
		restrict: 'A',
		link: function($scope, element, $attrs) {
			element.columns = 0;
			element.lastRow = 0;
			element.gridItems = [];
			element.fillers = [];
			element.rendered = false;

			element.getColumns = function() {
				var container = this;
				return Math.max(2, Math.floor(container.width() / 200) * 2);
			};

			element.addFillers = function(matrix, rows) {
				var container = this;
				container.fillers = [];
				container.find('.filler').remove();
				for (var y = 0; y < rows; y++) {
					for (var x = 0; x < container.columns; x++) {
						if (matrix[y][x] === 0) {
							var filler = $('<div class="masonry-item filler"></div>');
							container.append(filler);
							container.fillers.push({
								'element': filler,
								'pos': {
									'top': y,
									'left': x
								}
							});
						}
					}
				}
			};

			element.render = function() {
				var container = this;
				var widgetMatrix = _matrix(1, container.columns, 0);
				container.lastRow = 0;
				for (var i = 0; i < container.gridItems.length; i++) {
					var pos = _generatePosition(widgetMatrix, container.columns, Math.min(container.gridItems[i].width, container.columns), container.gridItems[i].height);
					container.gridItems[i].pos = pos;
					if (pos.top + container.gridItems[i].height > container.lastRow) {
						container.lastRow = pos.top + container.gridItems[i].height;
					}
				}

				if (hasAttr(container, 'zh-masonry-fillers')) {
					container.addFillers(widgetMatrix, container.lastRow);
				}
				container.positionGridItems();
			};

			element.positionGridItems = function() {
				var container = this;
				var unitSize = Math.round(container.width() / container.columns);
				var padding = parseInt(container.css('padding-top'));
				container.css('height', container.lastRow * unitSize + padding * 2);
				unitSize = Math.round(container.width() / container.columns);
				var i, pos;
				for (i = 0; i < container.gridItems.length; i++) {
					var item = container.find('#masonry-item-' + container.gridItems[i].id);
					pos = container.gridItems[i].pos;
					item.addClass('masonry-item color' + (container.gridItems[i].color ? container.gridItems[i].color : 1));
					item.css('left', pos.left * unitSize + padding);
					item.css('top', pos.top * unitSize + padding);
					item.css('width', container.gridItems[i].width * unitSize);
					item.css('height', container.gridItems[i].height * unitSize);
					if (container.gridItems[i].redraw) {
						var redrawFunction = container.gridItems[i].redraw;
						$timeout(redrawFunction, 500);
					}
				}
				for (i = 0; i < container.fillers.length; i++) {
					var filler = container.fillers[i].element;
					pos = container.fillers[i].pos;
					filler.css('left', pos.left * unitSize + padding);
					filler.css('top', pos.top * unitSize + padding);
					filler.css('width', unitSize);
					filler.css('height', unitSize);
				}
			};

			element.attachEvents = function() {
				var container = this;
				container.on('dblclick', function () {
					container.edit = !container.edit;
				});
			};

			$scope.buildGrid = function(items) {
				$scope.grid = element;

				$scope.grid.gridItems = items.filter(function(item) {
					return item.isVisible && item.aclIsVisible;
				});

				$scope.grid.columns = $scope.grid.getColumns();
				$scope.grid.rendered = false;

				// attach editing events
				$scope.grid.attachEvents();

				$timeout(function() {
					$scope.grid.render();
					$scope.grid.rendered = true;
					masonryGrids.push($scope.grid);
				});
			};
		}
	};
});

