ZeedhiDirectives.directive('zhTaskMatrix', ["$timeout", function($timeout) {
	return {
		restrict: 'A',
		link: function($scope, element) {

			// Sets maximum x-axis and y-axis values
			var maxY = $scope.widget.maxY || 10;
			var maxX = $scope.widget.maxX || 10;

			var clickedElems = [];

			// Creates a DOM reference matrix
			var matrix = new Array(maxX);
			for (var i=0; i<maxX; i++) {
				matrix[i] = new Array(maxY);
				for (var j=0; j<maxY; j++) {
					matrix[i][j] = [];
				}
			}

			function moveRow(evt) {
				var row;
				var xValue = $(evt.target).attr("xValue");
				var yValue = $(evt.target).attr("yValue");

				matrix[xValue][yValue].forEach(function(item) {
					if(item.elem[0] == evt.target) {
						$scope.widget.setCurrentRow(item.row);
						dismissSelection();
					}
				});
			}

			function dismissSelection() {
				element.find(".cover").css({"visibility":"hidden", "opacity": "0"});
				clickedElems.forEach(function(item) {
					item.elem.css({
						"z-index": "0",
						"margin-top": item.marginTop,
						"margin-left": item.marginLeft
					});
					item.elem.unbind("click");
					item.elem.click(selectData);
				});
				clickedElems = [];
			}

			function selectData(evt) {
				var elem = $(evt.target);
				var posx = elem.attr("xValue");
				var posy = elem.attr("yValue");

				if (matrix[posx][posy].length > 1) {
					// Shows cover
					element.find(".cover").css({"visibility":"visible", "opacity": "0.75"});

					// Sets clicked group
					clickedElems = matrix[posx][posy];

					// Styles the elements
					matrix[posx][posy].forEach(function(item, i) {
						item.marginTop  = item.elem.css("margin-top");
						item.marginLeft = item.elem.css("margin-left");

						item.elem.css({
							"z-index": "1",
							"margin-top": "-18px",
							"margin-left": Math.pow(-1,i+1) * Math.floor((i+1)/2) * (parseInt(item.elem.width()) + 10) - 12
						});

						item.elem.unbind("click");
						item.elem.click(moveRow);
					});
				} else {
					moveRow(evt);
				}
			}

			function loadData() {
				var wrapper = element.find(".data-squares").empty();
				$scope.widget.dataSource.data.forEach(function(row, index) {

					// Initial values
					var xValue = row[$scope.widget.xField];
					var yValue = row[$scope.widget.yField];
					var color  = row[$scope.widget.colorField];
					var displacement = matrix[xValue][yValue].length * 4;

					// Creates element and puts it's reference in the matrix
					var elem = angular.element("<div></div>");
					matrix[xValue][yValue].push({
						"elem": elem,
						"row": row,
						"index": index
					});

					elem.html(row[$scope.widget.valueField]);

					elem.attr("xValue", xValue);
					elem.attr("yValue", yValue);

					// Sets element styles
					elem.addClass("task");
					elem.css({
						"top": (100 - ((100 / maxY) * yValue)) + "%",
						"left": ((100 / maxX) * xValue) + "%",
						"margin-top": displacement - 18 + "px",
						"margin-left": "-18px",
						"backgroundColor": color || "#e2a53b"
					});

					// Binds a click function to elements
					$(elem).click(selectData);
					element.find(".cover").click(dismissSelection);

					// Appends element to DOM
					wrapper.append(elem);
				});
			}

			function generateScale() {
				var xAxis = element.find(".x-axis-scale").empty();
				var elem;
				for(var j=1; j<maxX; j++) {
					elem = angular.element("<span></span>");
					elem.css({
						"left": j * (100 / maxX) + "%"
					});
					elem.html(j);
					xAxis.append(elem);
				}

				var yAxis = element.find(".y-axis-scale").empty();
				for(var k=1; k<maxY; k++) {
					elem = angular.element("<span></span>");
					elem.css({
						"bottom": k * (100 / maxY) + "%"
					});
					elem.html(k);
					yAxis.append(elem);
				}
			}

			$scope.$watch("widget.dataSource.data", function() {
				generateScale();
				loadData();
			});
		}
	};
}]);

ZeedhiDirectives.directive('zhCalendar', function() {
	return {
		restrict: 'A',
		link: function($scope, element) {

			$scope.today = moment().startOf('day');
			$scope.currentDate = moment().startOf('month').startOf('day');

			$scope.todayText = $scope.widget.todayText || "Hoje";
			$scope.weekDays = $scope.widget.weekDays || ["dom", "seg", "ter", "qua", "qui", "sex", "sáb"];
			$scope.monthNames = $scope.widget.monthNames || ["Janeiro", "Fevereiro", "Março", "Abril", "Maio", "Junho", "Julho", "Agosto", "Setembro",
				"Outubro", "Novembro", "Dezembro"];

			function buildCalendarData() {
				// Clones the current date to prevent changing it
				var current = $scope.currentDate.clone().startOf('week');

				// Checks if the first day of the month is also the first day of its week so we should put it on the second row of the calendar
				if (current._d.getTime() === $scope.currentDate.clone()._d.getTime()) {
					current.subtract(7, 'day');
				}

				// Iterates through the datasource seeking for this month events and builds an object grouping them
				var monthEvents = {};
				if ($scope.widget.dataSource && $scope.widget.dataSource.data) {
					$scope.widget.dataSource.data
						.filter(function(item) {
							var month = moment(item[$scope.widget.dateField], $scope.widget.dateFormat).get('month');
							var currMonth = current.get('month');
							return month == currMonth || month == currMonth + 1 || month == currMonth + 2;
						})
						.forEach(function(item) {
							var itemTime = moment(item[$scope.widget.dateField], $scope.widget.dateFormat).startOf('day')._d.getTime();

							if (!monthEvents[itemTime]) {
								monthEvents[itemTime] = [];
							}

							monthEvents[itemTime].push(item);
						});
				}

				// Start creating weeks
				$scope.weeks = [];
				for (var i = 0; i < 6; i++) {
					var week = {
						days: []
					};

					for (var j = 0; j < 7; j++) {
						week.days.push({
							day: current.date(),
							date: current.date() + "-" + (current.month() + 1) + "-" + current.year(),
							today: current._d.getTime() === $scope.today._d.getTime(),
							lastMonth: current.month() < $scope.currentDate.month(),
							nextMonth: current.month() > $scope.currentDate.month(),
							events: monthEvents[current._d.getTime()] || [],
							selected: false
						});
						current.add(1, 'day');
					}

					$scope.weeks.push(week);
				}
			}

			// Navigation methods
			$scope.navigateDate = function(value, unit) {
				$scope.currentDate.add(value, unit);
			};
			$scope.goToday = function() {
				$scope.currentDate = $scope.today.clone().startOf('month');
			};

			$scope.dateClick = function(day) {
				if ($scope.widget.dateClick) {
					$scope.widget.dateClick();
				}
				$scope.widget.setCurrentRow(day.date);
			};

			/** @todo: Implement a multi-date selection
			 *
			 *	$scope.selectDate = function(dayObj) {
			 *		var fullDate = dayObj.date + '-' + dayObj.month + '-' + dayObj.year;
			 *		element.find("td[data-date=" + fullDate + "]").toggleClass('selected');
			 *	};
			 *
			 **/

			$scope.$watch(
				function() {
					return $scope.currentDate._d;
				},
				function() {
					$scope.currentMonth = $scope.monthNames[$scope.currentDate.get('month')];
					$scope.currentYear = $scope.currentDate.get('year');
					buildCalendarData();
				},
				true
			);

			$scope.$watch(
				function() {
					return $scope.widget.dataSource.data;
				},
				function() {
					$scope.currentMonth = $scope.monthNames[$scope.currentDate.get('month')];
					$scope.currentYear = $scope.currentDate.get('year');
					buildCalendarData();
				}
			);

		}
	};
});

