ZeedhiInterations.directive('groupHeader', ["templateManager", function(templateManager) {
	var openGroups = [];
	var lastOpened;
	var openOpen = function() {

		openGroups.forEach(function(group) {
			var groupElement = $("[data-group-id=" + group + "]");
			openGroup(groupElement, group);
		});

		if (openGroups.length) {
			var element = $("[data-group-id=" + openGroups[0] + "]");
			var scrollElement = element.closest("[zh-scroller]");
			var scroller = scrollElement.attr("index");
			if (scroller !== void 0) {
				templateManager.scrollers[scroller].scrollToElement($("[data-group-id=" + lastOpened + "]")[0]);
			} else {
				var top = $("[data-group-id=" + lastOpened + "]").position().top;
				$(scrollElement).scrollTop(top);
			}
		}

	};

	function openGroup(element, id) {

		element.parent().toggleClass('opened');

		var productsContainer = element.next();
		var productsUl = productsContainer.children().eq(0);

		if (element.parent().hasClass('opened')) {

			if (openGroups.indexOf(id) === -1) {
				openGroups.push(id);
			}

			productsContainer[0].style.height = productsUl[0].clientHeight + 'px';
		} else {

			var index = openGroups.indexOf(id);

			if (index !== -1) {
				openGroups.splice(index, 1);
			}

			productsContainer[0].style.height = 0;
		}
	}

	return {
		restrict: 'C',
		link: function($scope, element, attrs, controller) {
			element.on('click', function() {
				openGroup(element, attrs.groupId);
			});

			Util.doAsync(function() {
				element.parent().children().find(".products li").on("click", function() {
					lastOpened = attrs.groupId;
				});
			});

			if ($scope.$last) {
				Util.doAsync(openOpen);
			}

			if (!$scope.widget.clearOpened) {
				$scope.widget.clearOpened = function() {
					lastOpened = undefined;
					openGroups = [];
				};
			}
		}
	};
}]);

