ZeedhiInterations.directive("zhGridRow", function(templateManager) {
	return {
		priority: 500,
		link: function($scope, iElm, iAttrs, controller) {

			iElm.on("click", function(e) {
				/* swipe tip positioning */
				function changeTipPosition() {
					// get active swipe
					var activeSwipe = $('.zh-swipe-inner').last();
					// if iElm is inside activeSwipe, nothing to do
					if (iElm.closest('.zh-swipe-inner') != activeSwipe) {
						// get swipe's tip
						var tip = activeSwipe.find('.swipe-tip');
						// get grid inner component to bind scroll event
						var inner = iElm.closest('.zh-inner');

						var scrollGrid = function() {
							var top = iElm.offset().top - activeSwipe.offset().top;
							tip.css('top', top);
							tip.css('display', top < (inner.offset().top - activeSwipe.offset().top - 20) ? 'none' : 'block');
						};

						// unbind, if binded, the event to prevent multiples events caused by
						// multiples clicks on same grid
						inner.off('scroll.grid');
						inner.on('scroll.grid', scrollGrid);

						// position the tip
						scrollGrid();
					}
				}

				// Tip positioning must only be called if clicked inside a grid
				if (iElm.parents('.zh-grid').length > 0) {
					var unbindSwipeOpened = $scope.$on('swipeOpened', function(e) {
						changeTipPosition();
						// unbind $on('swipeOpened')
						unbindSwipeOpened();
					});
				}

				$scope.setFormData($scope.widget, $scope.row);
				templateManager.menuVisible(false);
				$scope.$apply();
			});

		}
	};
});

ZeedhiInterations.directive("zhGanttContainer", function($timeout, $rootScope, $compile) {
	return {
		restrict: "C",
		link: function($scope, element, iAttrs, controller) {
			var innerFixedContainer = element.find('.table-container-fixed .zh-inner');
			innerFixedContainer.nonbounce();
			var innerContainer = element.find('.table-container .zh-inner');
			innerContainer.nonbounce();

			var scrollInnerFixedContainer = function() {
				innerContainer.off('scroll');
				innerContainer.scrollTop(innerFixedContainer.scrollTop());
				$timeout(function() {
					innerContainer.on('scroll', scrollInnerContainer);
				});
			};
			innerFixedContainer.on('scroll', scrollInnerFixedContainer);

			var scrollInnerContainer = function() {
				innerFixedContainer.off('scroll');
				innerFixedContainer.scrollTop(innerContainer.scrollTop());
				innerContainer.find("th div.zh-theader").css("margin-left", -9 - innerContainer.scrollLeft() + "px");
				$timeout(function() {
					innerFixedContainer.on('scroll', scrollInnerFixedContainer);
				});
			};
			innerContainer.on('scroll', scrollInnerContainer);

			function getDateInterval(widget) {
				var minDate, maxDate;
				var dateAux;
				var data = widget.dataSource.data;
				for (var i = 0; i < data.length; i++) {
					dateAux = new Date(data[i][widget.fieldPlanStart]);
					if (!minDate || dateAux < minDate) {
						minDate = dateAux;
					}
					if (data[i][widget.fieldRealStart]) {
						dateAux = new Date(data[i][widget.fieldRealStart]);
						if (!minDate || dateAux < minDate) {
							minDate = dateAux;
						}
					}
					dateAux = new Date(data[i][widget.fieldPlanEnd]);
					if (!maxDate || dateAux > maxDate) {
						maxDate = dateAux;
					}
					if (data[i][widget.fieldRealEnd]) {
						dateAux = new Date(data[i][widget.fieldRealEnd]);
						if (!maxDate || dateAux > maxDate) {
							maxDate = dateAux;
						}
					}
				}

				return {
					"min": minDate,
					"max": maxDate
				};
			}

			function getGanttSteps() {
				var steps = [];
				var parentSteps = [];
				var getDateLevel, getInitialDate, getEndDate, getNextStep, getStepLabel, getParentStep;

				var dateInterval = getDateInterval($scope.widget);

				var months = ['Jan', 'Fev', 'Mar', 'Abr', 'Mai', 'Jun', 'Jul', 'Ago', 'Set', 'Out', 'Nov', 'Dez'];

				function getDateOfISOWeek(y, w) {
					var simple = new Date(y, 0, 1 + (w - 1) * 7, 1, 0, 0);
					var dow = simple.getDay();
					var ISOweekStart = simple;
					if (dow <= 4)
						ISOweekStart.setDate(simple.getDate() - simple.getDay() + 1);
					else
						ISOweekStart.setDate(simple.getDate() + 8 - simple.getDay());
					return ISOweekStart;
				}

				function dateAdd(date, y, m, d) {
					return new Date(date.getFullYear() + y, date.getMonth() + m, date.getDate() + d, 12, 0, 0);
				}

				switch ($scope.zoomLevel) {
					case "YEAR":
						getDateLevel = function(d) {
							return d.getFullYear();
						};
						getInitialDate = function(y) {
							return new Date(y, 0, 1, 1, 0, 0);
						};
						getEndDate = function(y) {
							return new Date(y, 11, 31, 1, 0, 0);
						};
						getNextStep = function(y) {
							return y + 1;
						};
						getStepLabel = function(y) {
							return y.toString();
						};
						getParentStep = function(y) {
							return "ano";
						};
						break;
					case "QUARTER":
						getDateLevel = function(d) {
							return d.getFullYear() + 'T' + (Math.trunc(d.getMonth() / 3));
						};
						getInitialDate = function(q) {
							return new Date(parseInt(q.substr(0, 4)), parseInt(q.substr(5, 1)) * 3, 1, 1, 0, 0);
						};
						getEndDate = function(q) {
							return new Date(parseInt(q.substr(0, 4)), parseInt(q.substr(5, 1)) * 3 + 3, 0, 1, 0, 0);
						};
						getNextStep = function(q) {
							return getDateLevel(dateAdd(getInitialDate(q), 0, 3, 0));
						};
						getStepLabel = function(q) {
							return 'T' + (parseInt(q.substr(5, 1)) + 1);
						};
						getParentStep = function(q) {
							return q.substr(0, 4);
						};
						break;
					case "MONTH":
						getDateLevel = function(d) {
							return d.getFullYear() + 'M' + ('0' + d.getMonth()).substr(-2);
						};
						getInitialDate = function(m) {
							return new Date(parseInt(m.substr(0, 4)), parseInt(m.substr(5, 2)), 1, 1, 0, 0);
						};
						getEndDate = function(m) {
							return new Date(parseInt(m.substr(0, 4)), parseInt(m.substr(5, 2)) + 1, 0, 1, 0, 0);
						};
						getNextStep = function(m) {
							return getDateLevel(dateAdd(getInitialDate(m), 0, 1, 0));
						};
						getStepLabel = function(m) {
							return months[parseInt(m.substr(5, 2))];
						};
						getParentStep = function(m) {
							return m.substr(0, 4);
						};
						break;
					case "WEEK":
						getDateLevel = function(d) {
							var janOne = new Date(d.getFullYear(), 0, 1, 1, 0, 0);
							var weekNumber = Math.ceil((((d - janOne) / 86400000) + janOne.getDay()) / 7);
							return d.getFullYear() + 'S' + ('0' + weekNumber).substr(-2);
						};
						getInitialDate = function(w) {
							return getDateOfISOWeek(parseInt(w.substr(0, 4)), parseInt(w.substr(5, 2)));
						};
						getEndDate = function(w) {
							var nextWeek = getDateOfISOWeek(parseInt(w.substr(0, 4)), parseInt(w.substr(5, 2)) + 1);
							nextWeek.setDate(nextWeek.getDate() - 1);
							return nextWeek;
						};
						getNextStep = function(w) {
							return getDateLevel(dateAdd(getInitialDate(w), 0, 0, 7));
						};
						getStepLabel = function(w) {
							return w.substr(4, 3);
						};
						getParentStep = function(w) {
							return months[getDateOfISOWeek(parseInt(w.substr(0, 4)), parseInt(w.substr(5, 2))).getMonth()] + ' ' + parseInt(w.substr(0, 4));
						};
						break;
					case "DAY":
						getDateLevel = function(d) {
							return d.getFullYear() + 'M' + ('0' + d.getMonth()).substr(-2) + 'D' + ('0' + d.getDate()).substr(-2);
						};
						getInitialDate = function(d) {
							return new Date(parseInt(d.substr(0, 4)), parseInt(d.substr(5, 2)), parseInt(d.substr(8, 2)), 1, 0, 0);
						};
						getEndDate = function(d) {
							return new Date(parseInt(d.substr(0, 4)), parseInt(d.substr(5, 2)), parseInt(d.substr(8, 2)), 1, 0, 0);
						};
						getNextStep = function(d) {
							return getDateLevel(dateAdd(getInitialDate(d), 0, 0, 1));
						};
						getStepLabel = function(d) {
							return d.substr(8, 2);
						};
						getParentStep = function(d) {
							return months[getInitialDate(d).getMonth()] + ' ' + parseInt(d.substr(0, 4));
						};
						break;
				}

				var today = new Date().setHours(0, 0, 0, 0);
				var lastParent = null;
				lastInterval = null;
				for (var i = getDateLevel(dateInterval.min); i <= getDateLevel(dateInterval.max); i = getNextStep(i)) {
					steps.push({
						"label": getStepLabel(i),
						"begin": getInitialDate(i).setHours(0),
						"end": getEndDate(i).setHours(0),
						"firstChild": lastParent !== getParentStep(i),
						"today": lastInterval !== null && today >= lastInterval.begin && today <= lastInterval.end,
						"weekday": $scope.zoomLevel == "DAY" ? getInitialDate(i).getDay() : 'X'
					});
					lastInterval = {
						begin: getInitialDate(i).setHours(0),
						end: getEndDate(i).setHours(0)
					};

					if (lastParent !== getParentStep(i)) {
						lastParent = getParentStep(i);
						parentSteps.push({
							"label": lastParent,
							"colSpan": 1
						});
					} else {
						parentSteps[parentSteps.length - 1].colSpan = parentSteps[parentSteps.length - 1].colSpan + 1;
					}
				}

				return {
					"steps": steps,
					"parentSteps": parentSteps
				};
			}

			function cleanGantt() {
				element.find('.table-container table tbody').empty();
			}

			var building = false;

			function buildGantt(rowIdx) {
				var html = '',
					planned, real, late;
				var data = $scope.widget.plainTreeData;
				var steps = $scope.ganttSteps.steps;
				var inRange;

				if (rowIdx === 0) {
					cleanGantt();
				}

				if (rowIdx < data.length) {
					late = isLate(data[rowIdx]);

					html = '';
					planned = '';
					real = '';
					for (var j = 0; j < steps.length; j++) {
						inRange = isInRange(data[rowIdx], steps[j], 'planned');
						planned += '<td class="' + (inRange ? 'planned ' : '') + (steps[j].today ? 'today ' : '') + ('day' + steps[j].weekday) + '">';
						planned += '<span>&nbsp;</span>';
						planned += '</td>';

						inRange = isInRange(data[rowIdx], steps[j], 'real');
						real += '<td class="' + (inRange ? 'real ' : '') + (late ? 'late ' : '') + (steps[j].today ? 'today ' : '') + ('day' + steps[j].weekday) +
							'">';
						real += '<span>&nbsp;</span>';
						real += '</td>';
					}

					html += '<tr data-ng-class="{\'open\': widget.plainTreeData[' + rowIdx + ']._opened, \'closed\': !widget.plainTreeData[' + rowIdx +
						']._opened}" data-ng-show="canShowRow(widget.plainTreeData[' + rowIdx + '])">';
					html += planned;
					html += '</tr>';
					html += '<tr data-ng-class="{\'open\': widget.plainTreeData[' + rowIdx + ']._opened, \'closed\': !widget.plainTreeData[' + rowIdx +
						']._opened}" data-ng-show="canShowRow(widget.plainTreeData[' + rowIdx + '])">';
					html += real;
					html += '</tr>';

					element.find('.table-container table tbody').append($compile(html)($scope));

					$timeout(function() {
						buildGantt(rowIdx + 1);
					});
				} else {
					$rootScope.$broadcast("request.stop");
					building = false;
				}
			}

			function updateGantt() {
				if (!building) {
					building = true;
					if ($scope.widget.plainTreeData.length > 0) {
						$rootScope.$broadcast("request.start");
						$scope.ganttSteps = getGanttSteps();
						$timeout(function() {
							var rowIdx = 0;
							buildGantt(rowIdx);
						});
					} else {
						cleanGantt();
					}
				}
			}

			$scope.$watch('zoomLevel', function(newValue, oldValue) {
				if (newValue && newValue != oldValue) {
					updateGantt();
				}
			});
			$scope.$watch('widget.plainTreeData', function(value) {
				if ($scope.widget.fieldPlanStart && value) {
					updateGantt();
				}
			});

			function isInRange(row, step, type) {
				var inRange = false;
				var startDate, endDate;
				if (type == 'planned') {
					startDate = new Date(row[$scope.widget.fieldPlanStart]);
					endDate = new Date(row[$scope.widget.fieldPlanEnd]);
				} else {
					startDate = new Date(row[$scope.widget.fieldRealStart]);
					endDate = new Date(row[$scope.widget.fieldRealEnd]);
				}
				inRange = startDate >= step.begin && startDate <= step.end || endDate >= step.begin && endDate <= step.end || startDate <= step.begin &&
					endDate >= step.end;
				return inRange;
			}

			function isLate(row) {
				return row[$scope.widget.fieldLateStatus];
			}

			$scope.widget.changeGanttZoomLevel = function(level) {
				$scope.zoomLevel = level;
			};

			$scope.canShowRow = function(row) {
				if (!row) {
					return false;
				}
				if ($scope.searchList) {
					return $scope.controller.search(row);
				} else {
					return $scope.controller.isOpened(row) || row._parent === null;
				}
			};
		}
	};
});

ZeedhiInterations.directive('zhSlideForm', function(templateManager) {
	return {
		restrict: 'A',
		link: function($scope, $element, iAttrs, controller) {
			var widget = $scope.widget;
			if (widget) {
				widget.isOpen = false;

				widget.hideForm = function() {
					if (!widget.isFilterTab) {
						if ($element.hasClass("in") || $element.hasClass("infull") || $element.hasClass("out")) {
							$element.removeClass();
							widget.editing = false;
							$element.addClass("zh-widget slideGridForm outfull");
							widget.isSlidingForm = false;
						}
						widget.editing = false;
						widget.isOpen = false;
					}
				};

				widget.hideIfOpen = function() {
					if (widget.isOpen) {
						widget.hideForm();
					}
				};

				widget.middleHideForm = function() {
					if (!widget.isFilterTab) {
						$element.removeClass();
						$element.addClass("zh-widget slideGridForm out zh-shadow");
						widget.isSlidingForm = false;
						widget.isOpen = true;
					}
				};

				widget.middleShowForm = function() {
					if (!widget.isFilterTab) {
						$element.removeClass();
						$element.addClass("zh-widget slideGridForm infull zh-shadow");
						widget.isSlidingForm = true;
						$scope.$apply();
						widget.isOpen = true;
					}
				};

				widget.showForm = function() {
					if (!widget.isFilterTab) {
						$element.removeClass();
						$element.addClass("zh-widget slideGridForm in zh-shadow");
						widget.isSlidingForm = true;
						Util.doAsync(function() {
							widget.isOpen = true;
						});
					}
				};

				$scope.$watch('widget.editing', function(value) {
					if (value === true) {
						widget.showForm();
					} else if (value === false && ($element.hasClass('out') || $element.hasClass('in'))) {
						widget.hideForm();
					}
				});

				Zepto($element[0]).swipeRight(function(e) {
					e.stopPropagation();
					if ($(this).hasClass("infull")) {
						widget.middleHideForm();
					} else {
						widget.editing = false;
						widget.hideForm();
					}
					templateManager.menuVisible(false);
					$scope.$apply();
				});

				Zepto($element[0]).swipeLeft(function() {
					widget.middleShowForm();
				});

				$element.on("click", function() {
					templateManager.menuVisible(false);
					$scope.$apply();
				});
			}
		}
	};
});

