(function($, window, Zepto) {
	"use strict";

	var startY, startX;

	var defaults = {
		"$these": [],
		"touchstartInit": false,
		"touchmoveInit": false
	};

	$(".nonbounce").each(function() {
		defaults.$these.push($(this));
	});

	$.fn.nonbounce = function() {
		initTouchHandling();

		this.addClass('nonbounce');

		return this.each(function() {
			defaults.$these.push($(this));
		});
	};

	$.nonbounce = function() {
		initTouchHandling();
	};

	var scrollParent = function(evt) {
		var y = (evt.originalEvent.touches) ? evt.originalEvent.touches[0].screenY : evt.originalEvent.screenY;
		var nonbounce = $(evt.target).closest(".nonbounce");

		if (nonbounce) {
			nonbounce.scrollTop(nonbounce.scrollTop() + startY - y);
			startY = y;
		}

		evt.preventDefault();
	};

	var processXScroll = function(evt) {
		var x = (evt.originalEvent.touches) ? evt.originalEvent.touches[0].screenX : evt.originalEvent.screenX;
		var delta = startX - x;

		if (Math.abs(delta) > 0) {
			var nonbounce = $(evt.target).closest(".nonbounce");
			while (nonbounce.length > 0) {
				if (nonbounce[0].scrollWidth > nonbounce[0].clientWidth) {
					nonbounce.scrollLeft(nonbounce.scrollLeft() + delta);
					startX = x;
					break;
				} else {
					nonbounce = nonbounce.parent().closest(".nonbounce");
				}
			}

		}
	};

	var touchstart = function(evt) {
		evt = evt.originalEvent || evt;
		startY = (evt.touches) ? evt.touches[0].screenY : evt.screenY;
		startX = (evt.touches) ? evt.touches[0].screenX : evt.screenX;
	};

	var touchmove = function(evt) {
		if (!(evt.originalEvent.touches && evt.originalEvent.touches.length > 1)) {
			if ((evt.target.tagName && evt.target.tagName.toLowerCase() === 'input') &&
				(Zepto.os.phone || Zepto.os.tablet)) {
				scrollParent(evt);
			}

			// Prevents scrolling of all but the nonbounce elements
			if (!~$.inArray(true, $.map(defaults.$these, compareElem, evt.target))) {
				evt.preventDefault();
			}

			// Prevents scrolling of nonbounce element if bound conditions are met
			if (!hasCorrectBounds(evt)) {
				evt.preventDefault();
			}
		}

		processXScroll(evt);
	};

	var initTouchHandling = function() {
		if (!defaults.touchstartInit) {
			defaults.touchstartInit = true;
			$(window).on("touchstart", touchstart);
		}

		if (!defaults.touchmoveInit) {
			defaults.touchmoveInit = true;
			$(window).on("touchmove", touchmove);
		}
	};

	var compareElem = function($elem, i, target) {
		return !!$(target).closest($elem).length;
	};

	var hasCorrectBounds = function(evt) {
		var y = (evt.originalEvent.touches) ? evt.originalEvent.touches[0].screenY : evt.originalEvent.screenY;
		var nonbounce = $(evt.target).closest(".nonbounce")[0];

		if (!nonbounce) {
			return true;
		}

		// Prevents scrolling of content to top
		if (nonbounce.scrollTop === 0 && startY <= y) {
			return false;
		}

		// Prevents scrolling of content to bottom
		if (nonbounce.scrollHeight - nonbounce.offsetHeight <= nonbounce.scrollTop - 10 && startY >= y) {
			return false;
		}

		return true;
	};

})(jQuery, window, Zepto);

ZeedhiInterations.directive('zhScroller', ["$timeout", "templateManager", "$document", function($timeout, templateManager, $document) {
	return {
		restrict: 'A',
		link: function($scope, iElement, iAttrs) {
			var expectedHeight = 0;
			//Zed - adjust top and bottom dinamically
			var startScroller = function() {
				var windowHeight = $(window).height();
				var containerTop = iElement.offset().top;
				var containerBottom = 0;
				// if container has margin-top then adjust the top
				var containerMarginTop = parseFloat(iElement.css('margin-top') || window.getComputedStyle(iElement[0], false).marginTop);
				containerTop -= containerMarginTop;

				// defines the maximum height from the container. At least 260px and maximum of half window
				var maxContainerHeight = Math.ceil(windowHeight * 0.5) > 260 ? Math.ceil(windowHeight * 0.5) : 260;

				// verifies if his element has a container fixed on the bottom of page and adjusts the bottom
				var parent = iElement.parent();
				var fixedBottomContainer = parent.children(".container[zh-fixed-bottom]")[0];
				if (fixedBottomContainer) {
					containerBottom = fixedBottomContainer.clientHeight;
				}

				function hasAttr(element, attr) {
					return (typeof element.attr(attr) !== "undefined" && element.attr(attr) !== false) || (typeof element.attr('data-' + attr) !== "undefined" &&
						element.attr('data-' + attr) !== false);
				}

				// search for a parent container with position non static or another container[zh-scroller]
				while (parent && parent.css('position') === 'static' &&
					!(parent.hasClass('container') && hasAttr(parent, 'zh-scroller'))) {
					parent = parent.parent();
				}

				// if there is a parent non static then adjusts the top
				if (parent) {
					containerTop = containerTop - parent.offset().top;
				}

				var popup = iElement.closest('.popup-content').length > 0;
				if (!popup) {
					iElement.css('position', 'absolute');
					iElement.css('top', containerTop + 'px');
					iElement.css('bottom', containerBottom + 'px');
				} else {
					iElement.css('top', '0px');
					iElement.css('width', 'auto');
					iElement.css('overflow', 'auto');
					iElement.css('position', 'relative');
					if(Zepto.os.phone) {
						iElement.css('height', ( 0.9 * window.innerHeight - 100) + 'px');
					} else if (iElement.find('.calendar').length > 0 || iElement.find('.zh-grid').length > 0 || iElement.find('.zh-template-list').length > 0 || iElement.find('.zh-tab-wrapper').length > 0) {
						// popup with grids or lists or tabbed containers should not have dynamic height
						iElement.css('height', (maxContainerHeight - containerTop) + 'px');
					} else {
						iElement.css('max-height', (maxContainerHeight - containerTop) + 'px');
					}
				}

				expectedHeight = maxContainerHeight - containerTop;

				// prevent flickering due the reverse order of execution of this function thru the DOM tree
				iElement.animate({
					opacity: 1
				}, 250);

				// prevent bounce when scrolling outside div
				iElement.nonbounce();
				iElement._scrollStarted = true;
			};

			$scope.$watch(function() {
				return iElement.is(':visible');
			}, function(value) {
				if (value === true && !iElement._scrollStarted) {
					$timeout(startScroller, 100);
				}
			});

			var realHeight;
			$scope.$watch("searchOpened", function() {
				$timeout(function() {
					var popupScroller = iElement.parent("[zh-scroller]");
					if(popupScroller.length) {

						if(expectedHeight > window.innerHeight - 100) {
							popupScroller.css({
								"height": window.innerHeight - 100
							});
						} else {
							popupScroller.css({
								"height": expectedHeight
							});
						}
					}
				}, 1000);
			});


		},

		priority: 10
	};
}]);

