ZeedhiDirectives.directive('zhInfinityScroll', function(templateManager, Query) {
	return {
		link: function(scope, element, attrs) {
			var widget = scope.widget;
			var datasource = scope.widget.dataSource;
			element.infinityOldValue = "";
			element.loadedAllData = false;
			var scrollMethod = Util.buildDebounceMethod(function(e) {
				if (element.infinityOldValue !== attrs.zhInfinityScroll) {
					element.infinityOldValue = attrs.zhInfinityScroll;
					element.loadedAllData = false;
				}
				var itemsPerPage = widget.itemsPerPage || datasource.itemsPerPage;
				var maxCurrent = datasource.getCurrentPage() * itemsPerPage;
				var initialSize = datasource.data.length;
				if (!widget.hasPagination || (e.target.scrollTop + e.target.offsetHeight < e.target.scrollHeight) || initialSize < maxCurrent || element.loadedAllData) {
					return;
				}
				var limit = datasource.getNextPage() * itemsPerPage;
				var filters = widget.getDataSourceFilter();

				if (!Util.isEmptyOrBlank(attrs.zhInfinityScroll)) {
					filters = filters.concat(Query.build().where("*").likeAll("%" + attrs.zhInfinityScroll + "%").where());
				}
				datasource.load(filters, datasource.getCurrentPage(), itemsPerPage).then(function(data) {
					if (datasource.data.length !== initialSize) {
						templateManager.updateTemplate();
					} else if (datasource.data.length < limit) {
						datasource.getPrevPage();
					}
					if (datasource.data.length === initialSize || datasource.data.length < limit) {
						element.loadedAllData = true;
					}
					widget.updateCheckedRows();
				});
			}, 500);
			element.on('scroll.infinity', scrollMethod);
		}
	};
});

