ZeedhiInterations.directive('zhLongTap', function($timeout) {
	return {
		restrict: 'A',
		link: function($scope, $elm, $attrs) {
			$elm.bind('touchstart', function(evt) {
				// Locally scoped variable that will keep track of the long press
				$scope.longPress = true;
				$scope.preventClick = false;
				// We'll set a timeout for 600 ms for a long press
				$timeout(function() {
					if ($scope.longPress) {
						$scope.preventClick = true;
						// If the touchend event hasn't fired,
						// apply the function given in on the element's on-long-press attribute
						$scope.$apply(function() {
							$scope.$eval($attrs.zhLongTap);
						});
					}
				}, 600);
			});
			$elm.bind('touchmove', function(evt) {
				$scope.longPress = false;
			});
			$elm.bind('touchend', function(evt) {
				// Prevent the onLongPress event from firing
				$scope.longPress = false;
				$scope.$parent.fireClickEvent = true; //@todo improve on waiter
				if ($scope.preventClick) {
					$scope.$parent.fireClickEvent = false;
					evt.preventDefault();
					evt.stopPropagation();
				}
				// If there is an on-touch-end function attached to this element, apply it
				if ($attrs.onTouchEnd) {
					$scope.$apply(function() {
						$scope.$eval($attrs.onTouchEnd);
					});
				}
			});
		}
	};
});

