ZeedhiDirectives.directive('zhFileUpload', function(templateManager, ZHPromise) {
	return {
		link: function(scope, element, attrs) {
			var field = scope.field;
			var widget = scope.widget;
			var container = element.parent();

			field.fileList = [];
			if (field.isMultiple) {
				element.attr("multiple", "true");
			}

			element.parent().find("ul").nonbounce();

			var processFile = function(file) {
				var deferred = ZHPromise.defer();
				var fileReader = new FileReader();
				fileReader.onload = function(e) {
					widget.currentRow[field.name].push(Util.extend(file, {
						b64File: e.target.result
					}));
					field.fileList = widget.currentRow[field.name];
					deferred.resolve();
				};
				fileReader.readAsDataURL(file);
				return deferred;
			};

			element.on('change', function() {
				if (this.files.length > 0) {
					if (!widget.currentRow) {
						widget.currentRow = {};
					}
					if (!field.isMultiple || !Array.isArray(widget.currentRow[field.name])) {
						widget.currentRow[field.name] = [];
					}
					widget.currentRow.changed = true;
					var promises = [];
					for (var i in this.files) {
						if (this.files.hasOwnProperty(i) && this.files[i] instanceof File) {
							promises.push(processFile(this.files[i]));
						}
					}
					ZHPromise.all(promises).then(function() {
						field.validate(field.value());
						templateManager.updateTemplate();
					});
				}
				this.value = "";
			});
		}
	};
});


