ZeedhiServices.service("PropertyDefiner", function() {
	function Definer() {

		function accessorDescriptor(field, fun) {
			var desc = {
				enumerable: true,
				configurable: true
			};
			desc[field] = fun;
			return desc;
		}

		this.define = function(object) {

			Object.keys(object).filter(function(key) {
				return typeof object[key] === 'function' && (key.substring(0, 3) === 'get' || key.substring(0, 3) === 'set');
			}).forEach(function(key) {

				var getter = key.substring(3);
				getter = getter[0].toLowerCase() + getter.substring(1);

				if (Object.defineProperty) {
					Object.defineProperty(object, getter, accessorDescriptor(key.substring(0, 3), object[key]));
				} else if (Object.prototype.__defineGetter__) {
					object.__defineGetter__(getter, object[key]);
				} else {
					throw new Error("browser does not support getters");
				}
			});

			return object;
		};

		this.defineProperty = function(object, getter, setter, name) {
			Object.defineProperty(object, name, accessorDescriptor("get", getter));
			Object.defineProperty(object, name, accessorDescriptor("set", setter));
		};

	}
	return new Definer();
});

