ZeedhiServices.service("ZhSprite", function($http, $templateCache, ZHPromise) {
	var ZhSprite = function() {
		var self = this;
		var SPRITE = 'sprite';
		var SPRITE_URL = 'spriteUrl';
		var spriteDeferred = ZHPromise.defer();

		function makeSVG(tag, attrs) {
			var el = document.createElementNS('http://www.w3.org/2000/svg', tag);
			for (var k in attrs)
				el.setAttribute(k, attrs[k]);
			return el;
		}

		function buildPreloadIcon(icon){
			var svg = makeSVG(icon.svgTag, icon.attrs);
			if(icon.children){
				icon.children.forEach(function(child){
					$(svg).append(makeSVG(child.svgTag, child.attrs));
				});
			}
			return svg;
		}

		function preloadIcons() {
			var icons = [{
				id: 'border',
				svgTag: "path",
				attrs: {
					d: "M19.944,38.026c-9.886,0-17.928-8.043-17.928-17.929c0-9.885,8.042-17.928,17.928-17.928 c9.886,0,17.929,8.043,17.929,17.928C37.872,29.983,29.829,38.026,19.944,38.026L19.944,38.026z M19.944,2.967 c-9.446,0-17.131,7.685-17.131,17.131c0,9.447,7.685,17.132,17.131,17.132c9.446,0,17.132-7.685,17.132-17.132 C37.075,10.651,29.39,2.967,19.944,2.967L19.944,2.967z"
				}
			}];
			icons.forEach(function(icon) {
				$templateCache.put(icon.id, buildPreloadIcon(icon));
			});
		}

		this.loadSprite = function(spriteUrl) {
			if (!self.getSprite()) {
				$http.get(spriteUrl, {
					cache: true
				}).then(function(response) {
					$templateCache.put(SPRITE, jQuery.parseXML(response.data));
					spriteDeferred.resolve();
				}, spriteDeferred.reject);
			} else {
				spriteDeferred.resolve();
			}
			return spriteDeferred;
		};

		this.getSprite = function() {
			return $templateCache.get(SPRITE);
		};

		this.getIcon = function(iconName) {
			var iconElement = $templateCache.get(iconName);
			if (!iconElement) {
				iconElement = $templateCache.get(SPRITE).querySelector('#' + iconName);
				this.addIcon(iconName, iconElement);
			}
			return iconElement;
		};

		this.addIcon = function(iconName, svg) {
			$templateCache.put(iconName, svg);
		};

		this.setSpriteUrl = function(spriteUrl) {
			$templateCache.put(SPRITE_URL, spriteUrl);
		};

		this.getSpriteUrl = function() {
			return $templateCache.get(SPRITE_URL);
		};

		preloadIcons();
	};

	return new ZhSprite();
});

