function Memoizer(maxSize) {
	var keys = [];
	var values = [];

	this.add = function(key, value) {
		if (values.length > maxSize) {
			values.shift();
			keys.shift();
		}
		values.push(value);
		keys.push(key);
	};

	this.has = function(key) {
		return keys.indexOf(key) !== -1;
	};

	this.get = function(key) {
		var index = keys.indexOf(key);
		return values[index];
	};
}

