ZeedhiServices.service("ApplicationContext", function(validateEngine) {
	var ApplicationContext = {
		instanceKeys: [],
		instances: [],
		getArgsToFunction: function() {
			return {
				args: this.instances,
				keys: this.instanceKeys
			};
		},
		registerInstance: function(name, instance) {
			if (!ApplicationContext[name]) {
				ApplicationContext[name] = instance;
				ApplicationContext.instanceKeys.push(name);
				ApplicationContext.instances.push(instance);
			} else {
				throw new Error("[" + name + "] already registered!");
			}
		}
	};
	ApplicationContext.registerInstance('ValidationEngine', validateEngine);
	return ApplicationContext;
});

