ZeedhiServices.service("FactoryRegister", function(ApplicationContext, $injector) {
	function FactoryRegister() {
		function instantiate(service) {
			return $injector.instantiate(service, ApplicationContext);
		}

		var factories = [];

		this.register = function(entity, name, factory) {

			if (factory.name === "") {
				throw new Error("FactoryRegister: The factory registered with the name of '" + name + "' cannot be an anonymous function.");
			} else if (entity.name === "") {
				throw new Error("FactoryRegister: The entity produced by the " + factory.name + " cannot be an anonymous function.");
			}

			factory.prototype.ProducedClass = entity;
			var instantiated = instantiate(factory);
			ApplicationContext[name] = instantiated;
			factories[entity.name] = instantiated;
		};

		this.getAllFactories = function() {
			return factories;
		};
	}
	return new FactoryRegister();
});

